import{_ as e,c as i,a as n,b as s,e as l,r as c,o as r}from"./app-DXjYgPyf.js";const o={};function h(p,a){const t=c("LinkCard");return r(),i("div",null,[a[0]||(a[0]=n("p",{align:"center"},[n("img",{src:"https://miaobi-lite.bj.bcebos.com/miaobi/5mao/b'LV8xNzM2MzEwMjIzLjA2NTU0MDY%3D'/0.png?authorization=bce-auth-v1%2FALTAKmda7zOvhZVbRzBLewvCMU%2F2025-01-08T04%3A23%3A43Z%2F-1%2F%2F43a503bdd2d28ab070bbc6dfb8d26ad37adc56f3f6b039a4507144a65e101180",width:"300"}),n("br"),n("b",null,"《亲密关系》 - [美] 罗兰·米勒 (Rowland S. Miller)")],-1)),a[1]||(a[1]=n("blockquote",null,[n("p",null,"亲密关系与泛泛之交有什么区别？大丈夫与小女子真的般配吗？吸引力的秘密是什么？男人与女人真的是不同的动物吗？同性恋真的是由基因决定的吗？单亲家庭的孩子长大后更容易离婚吗……什么是爱情？由什么构成？能持续多久？两性在发生一夜情及选择终身伴侣上有什么差异？爱情和性欲是由不同的脑区控制吗？亲密关系美满的秘诀是什么？有什么方法能让婚姻持续一生？米勒教授在本书中回答了这些问题，尤其澄清了通俗心理学所宣扬的经验之谈，甚至某些错误观点。")],-1)),a[2]||(a[2]=n("blockquote",null,[n("p",null,"本书汲取了社会心理学、沟通研究、家庭研究、认知心理学、发展心理学、演化心理学、社会学、传播学及家政学等学科的最新成果，研究实践和理论建构并重，学术标准与大众兴趣兼备。全书结构清晰、逻辑严密、语言生动、启发思考，既通俗易懂，读来轻松愉快，又科学权威，崇尚实证精神。")],-1)),a[3]||(a[3]=n("blockquote",null,[n("p",null,"本书遵循由浅入深、由一般到特殊的认知规律，论述了亲密关系的基础、活动形态、类型、矛盾和修复等内容，读完本书，你将对人际吸引、爱情、婚姻、承诺、友谊、激情、沟通、性爱、依恋、择偶、嫉妒、出轨、家暴等亲密关系的方方面面有全新的认识。")],-1)),a[4]||(a[4]=n("blockquote",null,[n("p",null,"亲密关系是人类经验的核心，处理得好能给人带来极大的快乐，处理得不好则会造成重大创伤，因此科学地认识亲密关系，攸关我们每个人的幸福。本书既适合研究亲密关系的专业人士，能给他们带来启发与灵感，也适合每个想爱情甜蜜、婚姻长久、人生幸福的普通读者。")],-1)),s(t,{title:"Z-Library: 《亲密关系》",href:"https://openzlib.link/book/14001319/e66a92/%E4%BA%B2%E5%AF%86%E5%85%B3%E7%B3%BB.html"}),a[5]||(a[5]=l('<h2 id="第1章-人际关系的构成" tabindex="-1"><a class="header-anchor" href="#第1章-人际关系的构成"><span>第1章 人际关系的构成</span></a></h2><div class="hint-container note"><p class="hint-container-title">人类是非常社会化的动物。如果剥夺了和他人的紧密接触，这会令人很痛苦，人类社会属性的核心部分正是对亲密关系的需要。</p></div><h3 id="亲密关系的性质" tabindex="-1"><a class="header-anchor" href="#亲密关系的性质"><span>亲密关系的性质</span></a></h3><div class="hint-container note"><p class="hint-container-title">首先，亲密的伴侣彼此间有着广泛而私密的了解。他们熟知彼此的经历、爱好、情感和心愿，而且一般不会把这些信息透露给其他人。亲密的伴侣关心对方，彼此能从对方身上感受到更多的关爱。如果人们认为自己的伴侣了解、理解并欣赏自己，其亲密程度就会增加。</p></div><div class="hint-container note"><p class="hint-container-title">所以，并不存在单一的亲密关系模式。的确，我们需要记住人际关系最简单、最基本的特点是：种类多样，规格不齐。这种多样性使人际关系非常复杂，也使人际关系魅力无穷。</p></div><h3 id="归属需要" tabindex="-1"><a class="header-anchor" href="#归属需要"><span>归属需要</span></a></h3><div class="hint-container note"><p class="hint-container-title">归属需要使“我们与亲密之人正常的社会交往”变得必不可少。为满足归属需要，我们努力与他人建立和维持亲密的人际关系，我们还期待与那些了解、关心我们的人交往和沟通。个体需要的亲密关系无需太多，几个便可。归属需要得到满足后，我们建立人际关系的内驱力就会降低（因而人际关系的质量比数量更重要）。归属需要也和我们伴侣是谁并无太大的关系，只要他们能给予我们持续的关爱和包容，我们的归属需要就能得到满足。因而，即使一段重要的亲密关系终结，我们也往往能找到替代伴侣（尽管新人和旧人有很大的差别），而且能满足我们的归属需要。</p></div><div class="hint-container note"><p class="hint-container-title">为什么我们这样强烈地需要亲密关系？为什么我们是如此社会化的动物？可能的解释是：归属需要是人类长期演化的产物，逐渐成为所有人共同的自然倾向。这种观点的逻辑在于，由于早期人类生活在很小的部落群族里，生存环境恶劣，到处是长着獠牙利齿的猛兽。所以，孤僻的人比合群的人在繁衍子女和养育后代的成功率上更低。这种环境下，与他人建立稳定持续而充满关爱的人际关系的个性倾向就具有演化学上的适应意义，拥有这些个性倾向的早期人类，其子女更可能生存和繁衍。结果人类的特质也缓慢地演化为：十分在乎他人对自己的看法，并极力寻求他人的认同和亲密接触。虽然这种观点启发了我们对人类自身的现代行为进行反思（本章稍后会进一步探讨），但必须承认这只是一种猜测。无论这种演化论的假设是否完全正确，毫无疑问今天几乎所有人都十分关注自己与他人依恋关系的质量。如果我们的生命缺乏亲密感情，我们就很容易迷失人生方向，患上身心疾病。众所周知，衣食住行乃生活必需，但归属需要却表明亲密感情也是美好生活所必需的。</p></div><h3 id="亲密关系变化的根源" tabindex="-1"><a class="header-anchor" href="#亲密关系变化的根源"><span>亲密关系变化的根源</span></a></h3><div class="hint-container note"><p class="hint-container-title">所以，支配亲密关系的规范已经不同于上一代人的规范，原因很多。影响因素可能涉及经济发展水平。社会工业化程度越高、越富足，就越能接纳单身、包容离婚和支持晚婚，而全世界的社会经济发展水平都提高了。教育和财政资源的充裕，足以让人们变得更为独立，与过去相比，女性尤其更可能不结婚。而最近全球经济的衰退也有明显的影响。随着更多的情侣为节省金钱生活在一起，同居人数明显增加，但结婚率在赤贫人群中却在下降，没有稳定的收入人们并不愿意结婚。</p></div><div class="hint-container note"><p class="hint-container-title">在互联网上，我们能无限地接触天各一方的伙伴，自由地披露或掩盖消息，更有时间静坐下来思考我们的主张。影响人们面对面交往的一些重要品质，如相貌吸引力、地理位置的邻近等，在网络关系中却无关紧要（至少在开始是这样） 。不敢与人接触的害羞和内向的人，在网上却可能活泼开朗。</p></div><div class="hint-container note"><p class="hint-container-title">人们可能意识不到性别比率变化的重要性。高性别比率的社会（女性较少）倾向于支持老式、传统的两性性别角色，即男主外女主内，妻子在家相夫教子，丈夫外出工作养家。这种文化形态在性生活上也相对保守。理想的新娘应该是处女，未婚先孕让人蒙羞，公开同居几乎绝迹。妇女更早结婚，离婚不受欢迎。相形之下，低性别比率的社会（男性较少）则倾向于颠覆传统，也更为宽容。鼓励妇女外出工作以自立，默许（虽不提倡）婚外性关系的存在。如果不慎怀孕，成为未婚妈妈也无可厚非。各个历史时期的具体情形可能有所不同，但这种基本模式却是贯穿整个历史的。古罗马时代性别比率低，以骄奢淫逸而闻名。维多利亚时期的英格兰性别比率高，以贞洁守礼而著称。美国喧嚣的20世纪20年代性别比率低，是随心所欲、恣意寻欢的十年。那么出现“性解放”和“女权运动”的20世纪60年代晚期，性别比率高还是低？答案是非常低。</p></div><h3 id="个人经历的影响" tabindex="-1"><a class="header-anchor" href="#个人经历的影响"><span>个人经历的影响</span></a></h3><div class="hint-container note"><p class="hint-container-title">人际关系还受到个体经历和经验的影响，最好的例子就是对人际关系总体取向有重大影响的依恋类型（attachment styles）。发展心理学家（如Bowlby，1969）很早就发现，婴儿对其主要照料者（常为妈妈）的依赖表现出不同的模式。人们普遍认为，某些婴儿只要饥饿、尿床和受到惊吓，就能发现马上会得到悉心的照顾和呵护。婴儿哭喊时，慈爱的照料者总是如约而至，得到此种呵护的小宝贝们就能舒心地依赖他人，觉得他人可以信任，能从别人那里获得安全和友善。结果，这些儿童就发展出安全型（secure）依恋：他们快乐地与他人交往，很容易与他人发展出轻松信任的人际关系。</p></div><div class="hint-container note"><p class="hint-container-title">其他婴儿的情形可能不同。如果大人对孩子的照料无法预测而且并不持续，照料者有时热情关注，有时却心不在焉、焦急烦躁，有时根本就不出现。这些孩子就会对他人产生焦虑、复杂的情感，这种依恋类型就是焦虑—矛盾型（anxiousambivalent）。这些孩子由于不能确定照料者是否以及何时会回来关照自己，与他人的关系就会变得紧张和过分依赖，表现出对他人的过分贪求。</p></div><div class="hint-container note"><p class="hint-container-title">最后第三组婴儿的照料者在关照孩子时，带着拒绝或敌对的态度勉强为之。孩子就会认为他人是靠不住的，因而在与他人的关系上畏缩不前，表现出回避型（avoidant）依恋。回避依恋型的孩子经常怀疑和迁怒他人，不容易形成信任和亲密的人际关系。</p></div><div class="hint-container note"><p class="hint-container-title">当儿童面对危险的陌生环境时，安全型的儿童会跑向妈妈，很快安静下来，然后开始勇敢地探索陌生的新环境（Ainsworth et al.，1978）。焦虑—矛盾型的儿童则会大哭大闹，紧紧抱住妈妈，完全不顾父母的安全保证。而回避型的儿童往往躲开大人，保持和父母的距离，即使在害怕时也不愿意发生亲密接触。</p></div><div class="hint-container note"><p class="hint-container-title">因此，巴塞洛缪认为成人有四种依恋类型（见表1.1）。第一种安全型（secure），和儿童的安全型依恋完全相同。第二种痴迷型（preoccupied），是巴塞洛缪给焦虑矛盾型的新名称，因为这种类型的人若要感觉心安，就得过分地依赖于他人的赞许，所以他们过度地寻求认同，沉溺于人际关系，担心关系破裂。第三和第四种依恋类型反映了两种不同的“回避型”。恐惧型（fearful）的人因为害怕被拒绝而极力避免和他人发生亲密关系。虽然他们希望有人喜欢自己，但更担心自己因此离不开别人。相反，疏离型（dismissing）的人认为和他人发生亲密关系得不偿失。他们拒绝和他人相互依赖，因为他们相信自己能自力更生，也不在乎他人是否喜欢自己。</p></div><div class="hint-container note"><p class="hint-container-title">然而，我们并不会被动地受童年经验的束缚，因为依恋类型不断地受到我们成人后经历的影响。依恋类型既然是习得的，就可能发生变化。随着时间的推移，依恋类型的确会发生新的改变。一次悲痛欲绝的分手会让原本安全型的人不再安全，一段如胶似漆的恋情也能慢慢让回避亲密的人不再怀疑和戒备亲密感情。在两年之内，足有三分之一的依恋类型可以发生实质性变化。</p></div><h3 id="个体差异的影响" tabindex="-1"><a class="header-anchor" href="#个体差异的影响"><span>个体差异的影响</span></a></h3><h3 id="性别差异" tabindex="-1"><a class="header-anchor" href="#性别差异"><span>性别差异</span></a></h3><div class="hint-container note"><p class="hint-container-title">归根结底，由于两性之间的共性如此之多，所以在人际关系科学研究的许多维度和方面，其相似性远大于差异性。其实，“性别差异”这种说法具有误导性，因为它只强调两性之间的差异性，而忽略相似性，这容易使人产生错误的认识。所以，宣扬男人和女人来自不同的星球根本就是误导人，因为它根本就不对。“研究不支持男人和女人来自不同文化的观点，更别提来自不同的世界了”。根据你在本书中接触到的严谨的人际关系学，更精确的说法是“男人来自北科州，女人来自南科州”，或者正如我们某天看到的汽车贴纸所写：“男人来自地球，女人也来自地球，好好相处吧！”</p></div><h3 id="性认同差异" tabindex="-1"><a class="header-anchor" href="#性认同差异"><span>性认同差异</span></a></h3><div class="hint-container note"><p class="hint-container-title">性认同最好的例子是性别角色，即社会文化所期待的男女两性应有的“正常”行为模式。男人当然应该有“男子气”，他们应该自信、独立、果敢、能干、好强。女人就应该有“女人味”，热情、敏感、多情、友善。大多数人都认为男人和女人是相反的两性，因此全世界都不同程度地期望男性和女性有各自不同的独特社会行为。然而遗传在决定人们自信或友善的倾向时只起到四分之一到三分之一的作用，大部分行为是后天习得的。文化的影响持续而广泛，它通过社会化和榜样学习（而非生物学上的性别差异），促使我们期待所有的男性都应该有阳刚之气，所有的女性都应该温柔细腻。</p></div><div class="hint-container note"><p class="hint-container-title">双性化的人可能是这样一个人：在职场激烈的薪酬谈判中能有效而强悍地捍卫自己的利益；但回家后又能细腻而温柔地安慰刚刚失去宠物的孩子。大多数人只擅长一种技能，在一种情境下显得游刃有余，另一种情境下就不会那么轻松。而双性化的人在两种情况下都能驾驭自如。</p></div><div class="hint-container note"><p class="hint-container-title">注</p><p>我曾在电子邮箱中看到过这样一则笑话。</p><p>怎样吸引女人：</p><p>夸赞她，依偎她，亲吻她，爱抚她，热爱她，安慰她，保护她，拥抱她，不惜千金买笑，奉出美酒佳肴，烦心的时候听她唠叨，微恙的时候悉心照料，永远和她待在一起，永远支持她的意见，为她走遍天涯海角。</p><p>怎样吸引男人：</p><p>脱光衣服，带上啤酒。</p><p>这个笑话很有趣，却有点不妥。它加强了人们的刻板印象：在亲密关系中，女性关注柔情蜜意，而男性只求肉欲的满足。实际上，两性对亲密关系的期望差异很小，他们根本不是“相反的”两类人。虽然两性内的个体之间可能差异很大，但男性和女性的平均差异却很小。男性和女性都希望自己的亲密伴侣富有爱心和温情。</p></div><h3 id="人格" tabindex="-1"><a class="header-anchor" href="#人格"><span>人格</span></a></h3><div class="hint-container note"><p class="hint-container-title">五大人格特质</p><p>一组基本的特质就能很好地区分人们在诸多方面（如行为、思维和情感等） 的差异（McCrae &amp; Costa，2010）。人格专家把这些关键特征称为大五特质。大五的大部分特质（并非所有） 对亲密关系有重大的影响。你认为下列哪项特质不太重要？</p><p>开放性（openness to experience）:富有想象力、不墨守成规、艺术气质，相对应的是拘泥、僵化和教条。</p><p>外倾性（extraversion）:开朗、合群、热情、喜欢社交，相对应的是谨慎、内敛及害羞。</p><p>尽责性（conscientiousness ）:勤劳、可依赖、有序，相对应的是不可靠、粗心大意。</p><p>宜人性（agreeableness ）:同情心、合作性、对人信任，相对应的是易怒、暴躁和充满敌意。</p><p>神经质（neuroticism）:善变、容易担忧、焦虑和愤怒的程度。</p><p>上述人格五大特质的次序是按重要性从低到高排列的，看上去第一条即开放性在亲密关系中作用甚微。开放性并非完全没有影响（Donnellan et al.，2004 ），但其他四种特质的影响要大得多</p></div><h3 id="自尊" tabindex="-1"><a class="header-anchor" href="#自尊"><span>自尊</span></a></h3><div class="hint-container note"><p class="hint-container-title">大多数人喜欢自己，也有人不喜欢自己。对自我的评价构成了自尊。如果对自己的能力和特质持正面评价，自尊水平就高；如果怀疑自己，自尊水平就低。因为高自尊的人一般比低自尊的人活得更健康、更幸福，所以人们普遍认为自我感觉良好有益身心。</p></div><div class="hint-container note"><p class="hint-container-title">如果他人喜欢我们，我们就喜欢自己；如果他人积极地对待我们并看重与我们的关系，自尊水平就高。然而，如果我们不能吸引别人的关注——如果别人似乎并不在意我们是否会介入他们的生活——自尊水平就低。</p></div><div class="hint-container note"><p class="hint-container-title">毋庸置疑，假设人们认为自己对异性有吸引力，则自我感觉就会良好。我们从他人那里感受到的尊重明显地影响到随后的自我评价。</p></div><h3 id="人类本性的影响" tabindex="-1"><a class="header-anchor" href="#人类本性的影响"><span>人类本性的影响</span></a></h3><div class="hint-container note"><p class="hint-container-title">显而易见，男女双方为生育孩子投入的最低限度的时间和精力有很大差异。对于男人，最低要求可能只是一次射精；如果有足够多的能生育的女伴，男人一生可以生养数百个孩子。而女人只有在绝经前才能生孩子，每生养一个孩子都需要投入大量的时间和精力。男女双方在生养孩子上的养育投入（parental investment）的生物学差别，引起了男女双方在选择配偶时进化出不同的策略。可以想见，考虑到女性的繁殖能力有限，那些认真挑选配偶的女性祖先繁殖就更为成功（她们有更多的孩子存活下来，又去繁殖自己的孩子），而那些挑选配偶不太认真的女性祖先繁殖就没有这么成功。男性刚好相反，那些追求和利用每一次交配机会的乱交式的男人繁殖就更为成功。如果他们频繁地变换性伴侣，其后代可能不容易存活，但是这种（养育）质量上的不足可以用（孩子）数量来弥补。所以，当今的妇女——正如这一演化学理论所预测的那样——在选择性伴侣时比男性谨慎得多。她们要求男性伴侣聪明、友善、有名望、性情稳定，而男性选择女性伴侣则不会这么苛刻，女性也不像男性那样对随便、不忠诚的性行为感兴趣。或许这种性别差异是长期演化的结果。</p></div><div class="hint-container note"><p class="hint-container-title">另一个繁殖上的性别差异是女性总是能确知某个孩子是否是自己亲生的。相比之下，男性则会遭遇父系不确定的困扰；除非他完全自信伴侣会忠于他，否则他不可能绝对确定伴侣生的孩子就是自己的孩子。或许正是这个原因，即使女人的不贞行为比男人少，男人对于红杏出墙的威胁分外警惕，这一点较女人为甚。这种性别差异也可能是长期演化的结果。</p></div><div class="hint-container note"><p class="hint-container-title">演化学的观点还能用来解释人们在短期和长期性行为策略上的不同。男女双方在短期的艳遇和长期、稳定的亲密关系中所追求的异性的特征存在差别。详而言之，男人比女人更渴望短期的性关系；更嗜好与多个伴侣保持短期的风流关系，建立新的亲密关系后，也比女性更早地发生性关系。结果是，男人在猎艳时，看起来性感且“容易”得手的女人特别有吸引力。然而，如果男人想结婚安顿下来，常常偏好贞洁的女人作为将来的伴侣，即使这个男人仍然认为在偶然的性关系中放荡的女子是最理想的目标。男人还常常追求年轻、漂亮的妻子。当男人想确立长期的亲密关系时，比女人更看重外貌，随着年纪增长，老男人更加喜欢与更年轻的女子结婚。</p></div><div class="hint-container note"><p class="hint-container-title">女人却表现出不同的模式。女人选择短期性伙伴时——尤其是发生婚外情时——更看重性感、有魅力、强势等有许多阳刚之气的男人。但当评价有潜力的丈夫时，都把经济前景作为首选，有稳定和较高收入和资源的男人想必能给她们的孩子带来安全的成长环境，即使这些男人并不是备选丈夫中最性感的家伙。一般而言，女性比男性更关注长期伴侣的经济前景和社会地位。</p></div><h3 id="人际关系的消极面" tabindex="-1"><a class="header-anchor" href="#人际关系的消极面"><span>人际关系的消极面</span></a></h3><div class="hint-container note"><p class="hint-container-title">那么为什么还要冒这种风险呢？因为我们人类是社会化的动物，我们需要彼此。没有与他人的亲密联系，我们就会枯萎和死亡。</p></div><h2 id="第2章-研究方法" tabindex="-1"><a class="header-anchor" href="#第2章-研究方法"><span>第2章 研究方法</span></a></h2><h2 id="第3章-吸引力" tabindex="-1"><a class="header-anchor" href="#第3章-吸引力"><span>第3章 吸引力</span></a></h2><h3 id="吸引力的基础-一种奖赏" tabindex="-1"><a class="header-anchor" href="#吸引力的基础-一种奖赏"><span>吸引力的基础：一种奖赏</span></a></h3><div class="hint-container note"><p class="hint-container-title">人与人之间产生吸引力最基本的假设是：他人的出现对于我们有奖赏意义（Clore &amp; Byrne，1974）。影响吸引力的奖赏（rewards）有两种类型：与他人交往产生的直接奖赏，和仅与他人有关的间接利益。直接奖赏指的是他人提供给我们的所有显而易见的愉悦。如果他人给予我们许多兴趣和赞许，我们就会对这种关注和接纳感到非常高兴。如果他人聪明又美丽，我们就会享受这些赏心悦目的个人特征。而如果他人能给我们带来物质利益如金钱或地位，我们更会乐于接受这些好处。多数情况下，人们提供的直接奖赏越多，对我们的吸引力就越强。</p></div><div class="hint-container note"><p class="hint-container-title">正如我们所看到的，人们常常被那些能为将来的后代提供生存优势的人所吸引，即使他们根本还没想过养育孩子的问题。</p></div><h3 id="方便-远亲不如近邻" tabindex="-1"><a class="header-anchor" href="#方便-远亲不如近邻"><span>方便：远亲不如近邻</span></a></h3><div class="hint-container note"><p class="hint-container-title">为什么空间上的临近有着如此大的影响力？答案之一是如果他人在我们身边，我们很容易得到他们提供的各种奖赏。如果其他条件相同，身边的伙伴比遥远的伙伴更有优势：与远距离的伙伴交往消耗的金钱和付出的努力——诸如手机话费、汽油费和路上的耗时等——使这样的人际交往比住家附近的交往成本更高。远距的人际关系奖赏价值也低；在文字或声音中表达出的爱意远不如脸颊上真实的一吻那样打动人。因而，分居两地的亲密关系一般不如朝夕相伴的亲密关系令人满意。</p></div><h3 id="熟识-重复接触" tabindex="-1"><a class="header-anchor" href="#熟识-重复接触"><span>熟识：重复接触</span></a></h3><div class="hint-container note"><p class="hint-container-title">因为临近常常能导致熟识，而熟识又引起喜欢，所以与他人频繁的接触不仅能使交往更方便，而且会使对方看起来更有吸引力（Reis et al.，2001）。</p></div><h3 id="吸引力的秘密" tabindex="-1"><a class="header-anchor" href="#吸引力的秘密"><span>吸引力的秘密</span></a></h3><div class="hint-container note"><p class="hint-container-title">当然，不同体型的吸引力也存在差异。男性认为体重正常、不肥不瘦、腰身明显细于臀部的女性身材最有诱惑力。最吸引人的腰臀比例的曲线比率0.7，即腰部比臀部细30%（见图3.3）；这种“沙漏”形状的身材对世界各地的男性都有吸引力。注多数情况下，人们都认为肥胖的女性不如身材苗条或正常的女性有魅力，但干瘦的女性并不比正常的女性更吸引男性。男性还喜欢丰满的乳房，但乳房的大小并不如它与女性身体的比例重要；曲线比率为0.75的腰胸比例最吸引男性，巨乳配上水桶般的粗腰并不能提升女性的吸引力。并且女性的腰臀比例比乳房大小更能影响男性对女性吸引力的判断。</p></div><div class="hint-container note"><p class="hint-container-title">如前所述，男性的吸引力稍显复杂。当腰部仅比臀部略窄，腰臀比例为0.9时，男性的身体最有吸引力。宽广的肩膀也有吸引力；肩臀比例大（约为1.2）、肌肉强壮的男性发生性关系的年龄更早，性伴侣也更多，这一点在全世界都一样。然而，男性光有好身材并不能吸引女性，除非他还有其他资源；只有当他能挣一份体面的薪水时，他的腰臀比例才会影响女性对他的评价。男性即使英俊但若贫穷，对女性并不那么有吸引力。</p></div><div class="hint-container note"><p class="hint-container-title">对外貌吸引力的判断显然是多层面的，还存在影响外貌吸引力的其他因素。男女两性都倾向于男方高于女方（Salska et al.，2008），所以在网络上高个男性的照片比矮个男性的照片能得到更多女性的回应。矮个（如162厘米）男子要在婚恋网站得到与高个（如186厘米）男子一样多的回应，只有赚更多的钱才能做到——多得多的钱。上例中的矮个男子每年必须多赚221 000美元才能与高个男子一样吸引女子的注意（Hitsch et al.，2010）。说到金钱，职场中高个子也能得到更多的尊重和更高的收入；如果不考虑年龄和性别，在美国和英国身高每增加1英寸（约2.54厘米），平均每年多挣789美元（Judge &amp; Cable，2004）。比如在各种职业中，身高185厘米的人每年比身高175厘米的人多挣3 156美元。</p></div><div class="hint-container note"><p class="hint-container-title">未来伴侣身体散发出的气味对女性的影响比男性更大（Herz &amp; Inzlicht，2002）。尽管如此，男性更喜欢漂亮女人散发出的体香（Thornhill et al.，2003）。在一项对体香的经典研究中，人们连续几天穿着同一件T恤衫睡觉，也没有使用任何香水。然后陌生的参与者嗅一下这些T恤衫，并挑出最吸引人的气味。面孔对称、有吸引力的人的气味明显地比面孔不对称的、没有吸引力的人的气味好闻，因为陌生人偏好俊美之人的体香，而不是相貌平常人的气味（Thornhill et al.，2003）。值得一提的是，异性恋的男性认为女性在排卵期的体味比月经周期的其他时间好闻（Gildersleeve et al.，2011），但他们不是很喜欢同性恋男性的气味，同性恋男性的体味对其他同性恋的男性比对异性恋男性更有吸引力（Martins et al.，2005）。这些研究结果并不是我编造出来的，所以显然还有一些微妙的因素在起作用。</p></div><div class="hint-container note"><p class="hint-container-title">长发的女性比短发的女性对男性更有吸引力（Knapp-Kline et al.，2005）。这类研究中，男性参与者要评价图片中某个女性的吸引力，她的头发通过计算机图像技术在图片中表现得长短不一。男性参与者对长头发的约会女伴更感兴趣，一定程度上是因为他们认为长头发的女人不太可能订婚或者结婚，更愿意在第一次约会时就与他们发生性关系（Boynton，2008）。但男性的胸毛再长也没有此种效果；女性更喜欢胸部光滑、少毛的男性，而非胸部毛茸茸的男性（Dixson et al.，2010）。</p></div><div class="hint-container note"><p class="hint-container-title">女性还喜欢聪明的男性（这对于读到这本书的大多数男性都应该是好消息）。在一项有趣的研究中，研究者对男性进行了智力测试，然后拍摄他们投掷飞盘、朗读新闻标题和思考火星生命的场景。当女性观看这些视频时，男性越聪明就越有吸引力（Prokosch et al.，2009）。原因可能是当男性企图打动女性时，会使用比日常谈话更华丽（即夸大）的词藻（Rosenberg &amp; Tunney，2008）。</p></div><div class="hint-container note"><p class="hint-container-title">最后，男女两性都认为穿红色衣服的约会对象更有吸引力。参与研究的两性都认为陌生的异性（而非同性）穿着红色（而非绿色或蓝色）的衬衫更性感、更有吸引力，而当女性穿着红衣服时，男性更愿意采取示爱行动，如请求约会、花费金钱（Elliot et al.，2010； Elliott &amp; Niesta，2008）。无怪乎情人节是红色的。</p></div><h3 id="长相吸引力的演化观" tabindex="-1"><a class="header-anchor" href="#长相吸引力的演化观"><span>长相吸引力的演化观</span></a></h3><div class="hint-container note"><p class="hint-container-title">伴随着月经周期，妇女的偏好存在一些微妙而又有点好色的变化（Gangestad et al.，2010b）。妇女只在每个月排卵前的几天才有可能受孕（见图3.4），这段时间妇女会发现男人的某些特征特别具有吸引力。当处在受孕期时，妇女比其他时间更偏爱男子气的面孔、低沉的嗓音、有着对称体型男子的体味以及更大胆、更傲慢、更有领袖魅力的行为（Thornhill &amp; Gangestad，2008）。所以，妇女在最有可能怀上孩子时易被自信果断、狂妄自大的男子吸引，即那些“举止更像浪荡子而非好父亲”的男子（Perrett，2010，p.104）。但在这个月的其他时间则偏爱温情脉脉、和善友好、谦虚保守的男子。如果女性没有排卵（如服用避孕药），这种周期性的变化则不会发生（Smith et al.，2009）。</p></div><div class="hint-container note"><p class="hint-container-title">这些行为模式使一些理论家确信，身体美貌的标准存在一个演化的过程（如Buss，2012）。推测看来，那些能成功地找到可受孕、强壮健康的配偶的早期人类比起随便找个配偶的早期人类更可能繁殖成功。因而，现代男性普遍偏爱体型对称、娃娃脸面孔、腰臀比例低的女性，现代（受孕期内的）女性普遍偏爱体型对称、男子气、精力充沛的男性。这种偏爱是演化而来的倾向，它根源于人类的本性而不是特定文化传承的结果。</p></div><h3 id="长相的重要性" tabindex="-1"><a class="header-anchor" href="#长相的重要性"><span>长相的重要性</span></a></h3><div class="hint-container note"><p class="hint-container-title">具体而言，全世界的男性都比女性更为关注恋人的长相吸引力（见图3.5）。如果要在单身网站上描述自己对伴侣的要求，男性会比女性更加露骨地表明自己要找一位漂亮的对象（Iyengar，2007），男性在决定接触对象时比女性更容易受长相的影响（Hitsch et al.，2010）。女性都知道男性会以貌取人，这可能是2010年美国91%的整形手术对象都是女性的原因（American Society of Plastic Surgeons，2011）。</p></div><h3 id="美丽-交织着的代价与收益" tabindex="-1"><a class="header-anchor" href="#美丽-交织着的代价与收益"><span>美丽：交织着的代价与收益</span></a></h3><div class="hint-container note"><p class="hint-container-title">人们的长相究竟对人际交往有怎样的影响？众所周知，美丽的女人比姿色平庸的女人的约会次数更多（Reis et al.，1980）。而且，与美女交往令男人如沐春风；他们交谈时滔滔不绝，忘我投入，觉得能与美女交往真是三生有幸。他们还会给可爱的女招待更多的小费（Lynn，2009）。英俊的男人也一样走运，他们能得到女人更多的微笑、交谈和积极情感（Ickes，2009）。</p></div><div class="hint-container note"><p class="hint-container-title">然而，在获得与异性交往的机会方面，男人长相的吸引力比女人的长相更重要（Reis et al.，1982）。实际上女人的美貌和她与男人交往的时间整体上看并无相关。美丽女人的确有很多约会，但姿色平庸的女人在群体活动中也会把大量时间都用在与男人交往上。相形之下，男人的长相和他们与女人交往的次数及时间存在相关。长相平庸的男人与英俊潇洒的男人相比，与女性任何形式的交往都更少。准此而论，长相吸引力对男人社交活动的影响要甚于女人。</p></div><h3 id="长相吸引力的匹配" tabindex="-1"><a class="header-anchor" href="#长相吸引力的匹配"><span>长相吸引力的匹配</span></a></h3><div class="hint-container note"><p class="hint-container-title">我们已经详尽地探讨了长相吸引力（这也说明其重要性），但还有最后一点要说明，就是长相吸引力在人际关系刚建立时的作用。人们或许都想找到俊美的伴侣，但最终结果通常是与自己容貌相当的人配成对（Hitsch et al.，2010）。已确定爱情关系的伴侣，他们的长相吸引力往往是类似的；即他们的容貌是相匹配的。这种现象称为匹配（matching）。</p></div><p></p><div class="hint-container note"><p class="hint-container-title">亲密关系越严肃、投入程度越多时，匹配就越明显。有时运气好的话人们偶尔能与长相胜于自己的人约会，但这种恋爱关系并不稳定，也很难发展到订婚，因为双方并不是“同一类人”（White，1980b）。这表明，即使每个人都希望自己的伴侣长相俊美，但只有那些容貌也俊美的人才能得偿所愿。长相确实俊美的人并不愿意和容貌平庸的家伙成为伴侣，同样我们普通人也不会愿意和长相“差于我们”的伴侣长相厮守（Lee et al，2008）。</p></div><div class="hint-container note"><p class="hint-container-title">因而，虽然不很浪漫，但相似的长相吸引力或许是筛选伴侣的一个指标。如果人们普遍珍视俊美的容颜，都满足于寻找长得最好看的伴侣（反过来这些伴侣也一样），伴侣的匹配就出现了（Montoya，2008）。因此，夫妻双方在长相吸引力上是非常接近的（Little et al.，2006）。再往后，长相吸引力在婚姻中所起的作用远不如当初把情侣们吸引在一起时那么重要（McNulty et al.，2008），但如果两个人看起来差异很大，亲密关系可能根本无法建立（Straaten et al.，2009）。</p></div><h3 id="礼尚往来-喜欢那些喜欢我们的人" tabindex="-1"><a class="header-anchor" href="#礼尚往来-喜欢那些喜欢我们的人"><span>礼尚往来：喜欢那些喜欢我们的人</span></a></h3><div class="hint-container note"><p class="hint-container-title">吸引力匹配现象表明，要想享有成功的亲密关系，我们应该追求最有可能回报我们的伴侣。实际上，大多数人就是这样做的。</p></div><div class="hint-container note"><p class="hint-container-title">我们期望伴侣接纳自己的可能性大小，很大程度上和自己的适配价值（mate value）即作为生殖伙伴的综合吸引力有关。高适配价值的人，有很多热烈的追求者，因此他们能够坚持自己对伴侣的高要求。他们的确能实现（Edlund &amp; Sagarin，2010）。例如，绝代佳人对男人有非常高的标准要求；她们不单想要一个能成为好父亲的友善绅士，也不只需要经济实力雄厚的性感猛男，她们希望伴侣拥有所有这些理想的条件（Buss &amp; Shackelford，2008）。如果她们的适配价值足够高，或许真能吸引到这么完美的伴侣。但如果她们高估了自己的期望值、盲目自信，就会不断地被人拒绝。于是她们可能重新调整自己的适配价值，并对他人接纳自己的可能性重新做出估计（Greitemeyer，2010）。</p></div><div class="hint-container note"><p class="hint-container-title">显然，喜欢那些喜欢我们的人这一倾向是与吸引力的奖赏模型一致的。这一倾向还符合平衡理论（balance theory）的观点，该理论认为人们期望他们的思想、感情和社交关系能够保持一致（Heider，1958）。如果两个人彼此都喜欢对方，他们的感情契合可以说取得了“平衡”。如果两个人都讨厌对方也是一种平衡状态，但如果某人喜欢一个人但得到的回报是厌恶时，就是一种不平衡状态。如果涉及三个人结果会怎样？在一项研究中，大学生会遇见一位实验者，要么友好地对待他们，要么粗暴地对待他们（Aronson &amp; Cope，1968）。随后，实验者的导师会走进来，对实验者的态度也是要么友好要么粗暴！然后大学生有机会帮导师做些事情。他们会怎样反应？如果这位导师友好地对待让大学生愉快的实验者，或者粗鲁地对待让大学生不快的实验者，这两种情况下大学生们都对这位导师更有好感——也就是说，这两种人际交往取得了平衡。这项研究基本上支持了人们偏爱在人际关系中保持平衡的观点。正是这个原因，如果我们获悉某人和我们一样厌恶其他人，我们就倾向于喜欢与我们态度一致的人（Bosson et al.，2006）；我们还常常指望敌人的敌人就是我们的朋友，即使我们还没见过他们。</p></div><div class="hint-container note"><p class="hint-container-title">怎样搭讪——最好的开场白</p><p>在商店购物时，你不时会碰到在校园见过的美女或帅哥，当你们目光相遇时，他（她）可能正在对你热情地微笑。你想与他 （她）结识，该说些什么呢？你需要的不只是说一声“嗨”然后期待对方的反应吧？或许说一些睿智的俏皮话比较合适：“你爸爸是面包师吗？你的这些面包可真不错啊。”</p><p>人们普遍认为幽默的试探是不错的开场白。的确，很多书建议你引用这些有趣的只言片语来成功搭讪（例如，Dweck &amp; Ivey，1998）。然而请小心这些建议，否则结果可能适得其反。一项研究仔细地比较了3种不同类型的开场白的效果，故作幽默或轻浮的一句话可能是最糟糕的开场白。</p><p>我们先区分一下幽默的开场白、“无害的”中性开场白（如“嗨”或“最近还好吧？”）和忠实地表明兴趣的直接开场白 （如“嗨，我有点尴尬，但我真想认识你”）。当女性观看录像中的男人使用这些开场白时，她们最不喜欢的是幽默的开场白（Kleinke &amp; Dean，1990）。更为重要的是，如果一位男子在单身酒吧里使用中性或直接的开场白，有70%的次数得到女性的肯定回应，而幽默式的开场白成功率只有24%（Cunningham，1989）。带有性挑逗意义的开场白（比如“虽然我不是完美男人，但我敢打赌能让你在床上疯狂”）则往往会带来更糟的后果（Cooper et al.，2007）。不用比较都知道：简单地问声好不会显得做作或鲁莽，是最聪明的搭讪方法。</p><p>那么，为什么很多关于开场白的书充斥着轻浮的语句？原因很简单，作者是男性。如果女人在单身酒吧里向男人说出一句幽默的开场白，90%的次数会得到积极回应。实际上，女人说出的任何一种开场白对男人的效果都很好；有研究发现女人每次只说一声“嗨”就能搭讪成功（Cunningham，1989）。男人喜欢女性主动示好（Enke，2011），不会在乎女人使用什么样的开场白，这就使得他们反过来高估女人对幽默开场白的喜欢程度。</p></div><h3 id="相似性-喜欢与我们相像的人" tabindex="-1"><a class="header-anchor" href="#相似性-喜欢与我们相像的人"><span>相似性：喜欢与我们相像的人</span></a></h3><div class="hint-container note"><p class="hint-container-title">吸引力受到人们相像程度的强烈影响。那些在家庭背景、性格、长相吸引力以及态度上相像的人比不相像的人更有可能互相吸引。</p></div><h3 id="匹配是广泛的过程" tabindex="-1"><a class="header-anchor" href="#匹配是广泛的过程"><span>匹配是广泛的过程</span></a></h3><div class="hint-container note"><p class="hint-container-title">也许看上去不太浪漫，但名望、财富、健康、才能和长相等等都像商品一样，能用来吸引心仪的伴侣。如果我们把匹配视为广泛的过程，不仅涉及长相吸引力，而且包括各种其他长处和特质，那么很显然，人们总是会与旗鼓相当的人配成一对，同性相吸（相类似的人彼此吸引对方）就发生了。</p></div><div class="hint-container note"><p class="hint-container-title">女性不必关心伴侣是否年轻，因为男性只要活着就有生殖能力。相反，鉴于女性在其后代所耗费的巨大养育投入，注她们应该找一位能够在漫长的孕期及哺乳期为她们遮风避雨、保护安全的伴侣；她们应该偏爱能保障母子幸福、拥有丰富资源、社会地位高的强势男人。的确如图3.7所示，女性比男性更关心伴侣的经济状况（Gustavsson et al.，2008），挥金如土的男人比吝啬的男人能吸引更性感的伴侣（Kruger，2008）。而且，女性对伴侣年龄的偏爱不会随着年龄增加而发生变化；女人终其一生都喜欢嫁给比自己略大几岁的男人（Dunn et al.，2010）。</p></div><h3 id="发现差异需要时间" tabindex="-1"><a class="header-anchor" href="#发现差异需要时间"><span>发现差异需要时间</span></a></h3><div class="hint-container note"><p class="hint-container-title">根据伯纳德·默斯坦（Bernard Murstein）的刺激—价值观—角色（stimulus-value-role）理论（Murstein，1987），我们会从伴侣那里获得三种不同的信息，它们会影响亲密关系的发展。当伴侣第一次相遇，彼此的吸引力主要建立在“刺激”信息基础上，包括年龄、性别、长相等明显的外部特征。随后就进入“价值观”阶段，吸引力取决于彼此态度和信念的相像程度，人们开始了解彼此是否喜欢同样的比萨、电影和度假方式（见图3.8）。再往后，“角色”的相容性才变得更重要，此时伴侣们最终发现他们在养育方式、事业、居家等基本的生活要务上是否一致。问题是伴侣们对彼此的政治观点、娱乐格调可以十分满意，但可能根本意识不到对于定居在什么地方、是否生养子女、生养几个存在根本的分歧。有时候重大的差异只有在婚后才凸现出来；尽管存在这些差异，夫妻仍可能生活在一起，但这并不是因为相异而相吸。</p></div><h3 id="知觉到的相像与真实的相像-挥之不去的错误知觉" tabindex="-1"><a class="header-anchor" href="#知觉到的相像与真实的相像-挥之不去的错误知觉"><span>知觉到的相像与真实的相像：挥之不去的错误知觉</span></a></h3><div class="hint-container note"><p class="hint-container-title">在某种程度上，人们似乎是和幻想中的伴侣（把伴侣刻画成类似于自己的精神配偶）结婚——他们爱上的是另一种人，却认为自己的伴侣就是这种人（Montoya &amp; Horton，2004）——当他们真正了解彼此在很多方面存在严重分歧时，或许会很失望。</p></div><h3 id="你就是我要成为的那种人" tabindex="-1"><a class="header-anchor" href="#你就是我要成为的那种人"><span>你就是我要成为的那种人</span></a></h3><div class="hint-container note"><p class="hint-container-title">人们还仰慕那些有能力和才华的人，这些特质是自己所没有并渴望拥有的。相像起作用的另一微妙之处在于，那些我们渴望成为的人对我们有吸引力。我们往往喜欢那些与我们的理想自我相像的人，即那些拥有我们期望得到却并不具备的品质的人。这种倾向性比较复杂，因为如果那些人超过了我们的理想自我，让我们相形见绌，就具有威胁意义和令人不快了（Herbst et al.，2003）。不过，只要其他人只比我们好那么一点点——那么他们带给我们的就是默默的鼓励而不是羞辱——我们就能被那些与我们当前状况的确有点差异的人所吸引（Klohnen &amp; Luo，2003）。不要夸大这种微妙作用。最有吸引力的伴侣仍然是那些在很多方面和我们相像，但又拥有我们通过努力就能实现的理想特征的人（Figueredo et al.，2006）。这样的人根本不是与我们相左的“异性”。但是只要差距不是过大，或许我们更喜欢自己想要成为的那种理想式的人物，而不是与我们目前非常相像的人。</p></div><h3 id="相异-还能相吸-互补性" tabindex="-1"><a class="header-anchor" href="#相异-还能相吸-互补性"><span>“相异”还能相吸：互补性</span></a></h3><div class="hint-container note"><p class="hint-container-title">相似的情形也出现在满足的爱情中：感到最多爱意和最少冲突的伴侣是那些在热情上相似而支配欲上不同的伴侣（Markey &amp; Markey，2007）。如果伴侣一方要在亲密关系起带头作用，当另一方愿意顺从时才最快乐。</p></div><h3 id="障碍-得不到的就喜欢" tabindex="-1"><a class="header-anchor" href="#障碍-得不到的就喜欢"><span>障碍：得不到的就喜欢</span></a></h3><div class="hint-container note"><p class="hint-container-title">这一原理显然会影响我们对亲密伴侣的感情。在未婚的青年男女身上，研究者有时会观察到一种有趣的行为模式，叫做罗密欧与朱丽叶效应（Romeo and Juliet effect）：父母越是干涉子女的恋爱自由，他们彼此之间就会越加相爱。这或许不仅仅是简单的相关；久而久之父母的干涉对年轻恋人日益增加的狂热之爱起着积极的推动作用（Driscoll et al.，1972）。当然，这种效应并非总会发生（Leslie et al.，1986），但父母在禁止孩子的恋爱自由时，的确要三思而后行。如果父母的干涉使孩子产生了抗拒心理，父母就可能无意中使这位被禁止接近的对象反而更有吸引力了。这种情况下父母最好的行为或许是温和地表达自己的不满，或者干脆听之任之，什么也不做。</p></div><h3 id="男女两性期望的理想伴侣" tabindex="-1"><a class="header-anchor" href="#男女两性期望的理想伴侣"><span>男女两性期望的理想伴侣</span></a></h3><div class="hint-container note"><p class="hint-container-title">注</p><p>在世界各地，人们评价未来的伴侣有三条基本标准（Tran et al.，2008）。如果可以选择，大多数人都期望伴侣有以下的特点：</p><ul><li><p>热情和忠诚，值得信赖、亲切友善、给予支持、善解人意；</p></li><li><p>吸引力和活力，长相俊美、性感、外向；</p></li><li><p>社会地位和资源，经济宽裕、生活安心。</p></li></ul><p>所有这些特点都是值得拥有的，但它们并非同等重要，其优先性取决于我们追求的是较随便、短期的露水恋情还是忠诚持久的浪漫爱情。</p></div><div class="hint-container note"><p class="hint-container-title">男女两性对短期性伙伴的要求，远不如对长期伴侣的要求那么挑剔（Fletcher et al.，2004）。比如，在偶尔的风流韵事中，两性都能接纳在智力、热情和收入水平上比较低的性伙伴，但要做夫妻显然要求要高得多（Buunk et al.，2002）。具体而言，女性在仔细考虑短期的情人时，只要情人有强壮的肌肉、性感、“能引起性激动”就可接受，不太亲切、可靠或通情达理都不太要紧（Frederick &amp; Haselton，2007）。即使脸上的疤痕并不能让丈夫更有吸引力，但却能增进情人的诱惑力（Burriss et al.，2009）。</p></div><div class="hint-container note"><p class="hint-container-title">但女性清醒地意识到那些充满魅力、富有男子气概的男性可以是令人无法抗拒的好情人，但常常不见得是能托付终身的好丈夫（Boothroyd et al.，2007）。女性挑选丈夫，更看重良好的品格而非英俊的外表。她们在思索持久的亲密关系时，更看重热情、忠诚、社会地位和经济资源标准，而不是长相吸引力和活力标准（Fletcher et al.，2004）。威望和成就变得比勇敢和强势更为重要（Kruger &amp; Fitzgerald，2011）。如果不能拥有所有这些特点，普通的女性更喜欢友善、体谅和富裕（不要求特别英俊）的男子，而不是英俊但贫穷，或者富有、英俊但冷漠、不忠的男子（Li，2008）。</p></div><div class="hint-container note"><p class="hint-container-title">男性的优先性则不同。和女性相同的是他们也看重热情和忠诚，但不同的是，在长期亲密关系中，他们更看重吸引力和活力而非社会地位和经济资源（Fletcher et al.，2004）。普通的男性更喜欢没什么金钱、友善、漂亮的女子，而不是有钱但爱抱怨或温柔但丑陋的女子（Li，2008）。</p></div><div class="hint-container note"><p class="hint-container-title">当然，我们在找寻亲密伴侣时，对这些标准的要求往往不得不折中。要找到（并赢得！）完美的伴侣来满足我们所有的期望是非常困难的。如果我们坚持要求自己的伴侣既要友善、体谅，又要非常俊美，还要富有阔绰，我们很可能会长期处在失望和沮丧之中（Tolmacz，2004）。所以，在评价未来的伴侣时，男人往往首先确保女友至少有普通的长相，然后再来寻找尽可能多的热情、友善、诚实、坦率、稳定、幽默和智慧等等特征（Li et al.，2002）。绝世容貌是男人所渴求的，但不如高水平的热情和忠诚重要（社会地位和经济资源屈居第三位）。女人往往首先确保男友至少有点金钱和前途，然后再来寻找尽可能多的热情、友善、诚实、坦率、稳定、幽默和智慧等等特征（Li et al.，2002）。金钱与财富是女人所渴求的，但不如高水平的热情和忠诚重要，长相排在第三位。</p></div><h2 id="第4章-社会认知" tabindex="-1"><a class="header-anchor" href="#第4章-社会认知"><span>第4章 社会认知</span></a></h2><h3 id="第一印象及其影响" tabindex="-1"><a class="header-anchor" href="#第一印象及其影响"><span>第一印象及其影响</span></a></h3><div class="hint-container note"><p class="hint-container-title">当我们首次与人会面时，很难客观公平地认识他们。相反，各种刻板印象和首因效应影响着我们对观察到的行为的解释。</p></div><div class="hint-container note"><p class="hint-container-title">实际上，对异性恋爱关系预测最准确的往往是女当事人的朋友（Loving，2006）。如果她的朋友赞成这段恋情，就很有可能持续，但如果他们认为这段恋情注定要失败，就可能真的告吹（Etcheverry &amp; Agnew，2004）。</p></div><h3 id="记忆" tabindex="-1"><a class="header-anchor" href="#记忆"><span>记忆</span></a></h3><div class="hint-container note"><p class="hint-container-title">人们往往以为自己的记忆是对过去事件的如实表征。具体而言，人们可能会信任生动的回忆，因为它们看来如此确凿而具体。但一项历经数年的研究（Della Sala，2010）清晰地表明：随着新事件的发生，即便貌似生动形象的记忆，人们也会对其进行篡改和更新。所以对过去事件的记忆往往混杂着过去真实发生了的和最近才知悉的信息。心理学家用重构性记忆（reconstructive memory）这一术语来描述随着新信息的获得人们会不断地修订和改写记忆。</p></div><h3 id="亲密关系中的印象管理" tabindex="-1"><a class="header-anchor" href="#亲密关系中的印象管理"><span>亲密关系中的印象管理</span></a></h3><div class="hint-container note"><p class="hint-container-title">自我监控（selfmonitoring）能力高的人善于调整自己的行为，以适应各种情境下的不同规范。他们对社会线索敏感，知道自己应该做什么，他们愿意也有能力灵活地调整自己的行为以适应当前的情境。相形之下，自我监控能力低的人不太在乎社会规范，缺少变通，应变能力差，故而所有情境下都表现出一成不变的行为，即使并不适合当前的情境仍保持原来固定的形象。故而，自我监控能力高的人其印象管理更为灵活多变（Fuglestad &amp; Snyder，2009）。</p></div><div class="hint-container note"><p class="hint-container-title">这些不同的印象管理风格导致了不同的交际圈子。由于自我监控能力高的人常能快速地转变形象以面对不同的人，他们比自我监控能力低的人有着更多的朋友，但和这些朋友的共同点并不多。注自我监控能力高的人身边有许多“活动专家”式的伙伴，他们是进行某些爱好和活动的好伙伴——如“网球伴儿”或“芭蕾舞伴”——但在活动之外朋友间并不见得心投意合（Leone &amp; Hawkins，2006）。高自我监控者能有技巧地尽量避开可能引发争执的话题，而专家式的朋友则能使他们真正地享受这些活动——但如果他们聚会，把所有这些朋友都请来，那么他们可能是鲜有共同点的形形色色的人。相形之下，低自我监控者必须更加费力地寻找才能找到更为相似的同伴。如果把低自我监控的人的所有朋友召集在一起（当然屈指可数），他们的共同点会更多。</p></div><div class="hint-container note"><p class="hint-container-title">印象管理风格上的差别随着时间流逝似乎变得更为重要。高自我监控者第一次见到别人时，比低自我监控者体验到更为紧密的交往；他们善于找到谈话的共同点，也擅长和人私聊（Snyder &amp; Simpson，1984）。高超的印象管理能力似乎有助于他们与许多不同的人轻松自在地交往。但另一方面，他们在每个朋友身上所花的时间较少，所以与低自我监控者相比，他们的人际关系维持的时间更短、投入程度也较小（Leone&amp; Howkins，2006）。人际关系刚开始时，高自我监控者所享有的社交优势，在关系稳固后却可能变成障碍（Wright et al.，2007）。</p></div><div class="hint-container note"><p class="hint-container-title">亲密关系中印象管理的第二个有趣特点是，尽管我们给好友和恋人营造的印象比为熟人或陌生人营造的更为重要，但是为了维持良好的形象，我们在前者所花的心思通常不如后者。我们不会总是惦记着如何给熟悉的人留下美好的印象，也不会一直努力在他们面前显得招人喜欢、德才兼备（Leary et al.，1994）。比如人们与伴侣认识的时间越长，他们在约会聚餐时，在洗手间里梳洗打扮自己所花费的时间就越少（Daly et al.，1983）。我们对在亲密伴侣面前所呈现形象的关注程度，反不如面对陌生人时更在意，这可能有很多原因（Leary &amp; Miller，2000）。举例来说，我们都知道自己的好友和爱人已经喜欢上我们，所以营造美好形象以赢得他们赞许的动机不足。也可能是因为他们十分了解我们，要改变他们的看法比较困难。不过，还有可能仅仅是因为我们变懒了。要表现最好的言谈举止需要专心和努力。有礼貌的举止通常意味着某种形式的自我约束。在已经了解并喜爱我们的人身边，我们可以放松，无拘无束。但这也意味着人们对亲密伴侣常比对普通人更为坦荡不羁（Miller，1997b）。在谈恋爱时显得非常端庄得体的人——不沐浴更衣、穿戴整齐后决不出来吃早饭——常常在结婚后变成这样的配偶：穿着内衣坐在桌旁，没有洗漱，又是抓痒又是剔牙，还叉走了最后一个炸面包圈。这很有讽刺意义。人们在谈恋爱时，凭借优雅的举止赢得浪漫伴侣的爱情后，却再也不肯努力在爱人面前保持风度（这或许是许多亲密关系中存在的重大问题，在第6章我们会详细探讨）。</p></div><h2 id="第5章-沟通" tabindex="-1"><a class="header-anchor" href="#第5章-沟通"><span>第5章 沟通</span></a></h2><div class="hint-container note"><p class="hint-container-title">沟通的起点是传递者的意图，即传递者希望表达的信息。问题在于传递者的意图是秘密的，只有传递者本人才知道。这些信息要传达给接受者，就必须编码成公开的、可观察到的言语或者非言语信息。信息传递者的心情、社交技能和周围环境的噪音干扰等许多因素都会影响这一过程。然后，接受者必须对传递者的行动进行解码，这里同样可能会发生干扰（Albright et al.，2004）。最终对接受者产生的影响，同样是私密性质的，只有接受者本人清楚。</p></div><h3 id="面部表情" tabindex="-1"><a class="header-anchor" href="#面部表情"><span>面部表情</span></a></h3><div class="hint-container note"><p class="hint-container-title">所以面部表情含义的普遍性使其包含非常丰富的信息——当然是在它们真实表现的情况下。遗憾的是，面部表情在非言语沟通中如此惹人注目，人们有时会刻意控制以掩饰自己真实的情感。有时，这样做是因为要遵守表现规则（display rules），即在特定情境下为适合文化规范而展示面部表情（Zaalberg et al.，2004）。我们至少可以采用四种方法来调节情绪的表达，以遵循这些表现规则。首先，我们可以增强表情，刻意夸大它们，好像我们体验到比实际感受更强烈的情感。如果打开礼物时你并不是很喜悦，但如果馈赠者在场，你会表现得非常高兴。其次，有时我们还会减弱表情，试图使情感显得没有实际上的那般强烈。因为我们的文化要求“男儿有泪不轻弹”，男人应该显得很坚毅，不太容易被悲剧电影打动。第三，我们会中和自己的表情，把所有感情掩盖起来。扑克牌高手就是这样喜怒不形于色，谁也猜不透他手里的牌。最后，我们可以伪装自己真实的情感，代之以完全不同的外显情绪。当另一名选手赢得了选美比赛冠军，走在最前面的亚军仍显得十分兴奋，那么几乎可以肯定她在掩饰自己真实的情感。</p></div><h3 id="注视行为" tabindex="-1"><a class="header-anchor" href="#注视行为"><span>注视行为</span></a></h3><div class="hint-container note"><p class="hint-container-title">根据注视行为还能确定沟通双方的关系属于哪一种类型。恋人比朋友会更多地相互注视，而朋友比一般熟人也会更多地相互注视（Kleinke，1986）。而且，如果陌生人在交往时更多地注视彼此的眼睛，比在一起时眼睛张望别处更加喜欢彼此（Kellerman et al.，1989）。沟通双方更多的注视行为，不仅能表露单纯的兴趣，而且能传递关爱之情。</p></div><p>### 身体接触</p><div class="hint-container note"><p class="hint-container-title">与他人的身体接触也有着丰富的含义。在很多文化中，人们首次相遇会彼此握手。众所周知，对方与你握手的力量、气势、时间和部位都传递着重要的信息。握手行为和一些人格特质有关；握手时满握、有力和持续时间长的人与软弱无力的握手者相比，往往更加外向和开放，神经质较低。握手有力的女性往往更随和（Chaplin et al.，2000）。</p></div><p>### 副语言</p><div class="hint-container note"><p class="hint-container-title">根据副语言还可以确定人际关系的性质，因为情人之间和朋友之间彼此谈话的节奏并不一样。情人之间延迟应答的时间更长，谈话间经常有沉默，总体上说得更少（Guerrero，1997）。即使面对陌生人，人们面对俊美的人和容貌平庸的人说话方式也不一样；男女两性在给俊美而非平庸的异性留下声音短信时都会使用更低频的声音，而且这样做他们感觉更愉悦（Hughes et al.，2010）。副语言是否有助于判断恋爱关系的开始？</p></div><div class="hint-container note"><p class="hint-container-title">打情骂俏</p><p>非言语沟通的极佳例子是人们故意吸引他人的注意力，并对其表达兴趣时所表现出来的行为，这种行为也常常引起人的误会。如果在大学校园酒吧里的女生希望有人接近自己，她很可能四处张望，走近吧台，并用手指梳理自己的秀发（Cunningham &amp; Barbee，2008）。但女性并不喜欢还没有引起自己注意的男性接近自己，所以男性先得让女性注意上他们。占据更大的空间、摆出开放的姿势、触碰其他的男人（不会反过来被人触碰），这些举止都是地位高的标志，能让男性在人群中脱颖而出。具有这些行为特征的男性——眼睛经常会扫视四周看看谁在注意自己——更可能和女性发生目光接触从而发生交谈（Renninger et al.，2004）。</p><p>然后，如果他们开始打情骂俏，男性和女性往往都有更多的微笑，更靠近，注视时间更长，更频繁地触碰对方，这比他们彼此缺乏兴趣时热烈得多（Moore，2010）。他们的言语更为生动活泼，有更多的笑声，沉默时间更少，声音听起来也很热情（Coker &amp; Burgoon，1987）。与这些兴趣信号和身体触碰同时出现的还有独特的身体动作，如头部倾斜、嘴巴撅起、扭捏作态等等（Simpson et al.，1993）。这些让人动心的行为一起清晰地表明个体想和新同伴继续交往。</p><p>单纯的调情行为和直截了当地勾引人的行为并不一样。与朋友式的调情相比，意图表达性兴趣的动作会有更多的视线接触、微笑、身体触碰、更亲密的副语言以及更小的人际距离（Koeppel et al.，1993）。但男性常常看不清楚这种区别，他们易于从女性的友好行为中体会到性暗示（Jacques-Tiura et al.，2007）。男性也比女性更可能从调情行为中品读出性动机和性意图，而女性表示搞笑、轻浮和喜庆（却没有更多其他含义）的行为一直容易让男性误解。另一方面，女性暗示性信号的信息也容易被误以为仅仅表示友好。男子这两种错误都会犯（Farris et al.，2008），因为他们在解读他人时一般并不如女子那般准确（Brody &amp; Hall，2010）。这就是生活。</p></div><h3 id="自我表露的发展过程" tabindex="-1"><a class="header-anchor" href="#自我表露的发展过程"><span>自我表露的发展过程</span></a></h3><div class="hint-container note"><p class="hint-container-title">人际关系的发展大体上就是这样（Derlega et al.，2008）。此外，与熟人早期交往的自我表露通常会表现出明显的相互作用（reciprocity）。新伙伴们倾向于匹配彼此的开放程度，如果对方表露多自己也表露多，如果对方的自我表露减少自己也表露更少。人们透露自己信息的程度，则往往取决于不同的对象，在不同的人际关系间也有很大的变化（Dindia，2002）。这往往也是个渐进的过程，新伙伴们通常分阶段地转到更深层次的话题，而不是一步到位。一次说太多、太快是危险的；因为这样会突破别人的期望，常常会给人留下糟糕的印象（Buck &amp; Plant，2011）。最好的策略通常是保持耐心，让可以感觉到的相互作用逐渐地增加你们交往的亲密感。</p></div><p>### 自我表露和亲密关系的满足</p><div class="hint-container note"><p class="hint-container-title">归根结底，与沟通情境相符合的自我表露能在亲密关系中孕育喜爱和满意。比如恋人彼此的自我表露越多，他们就感到越幸福（Sprecher &amp; Hendrick，2004）。的确，幸福的爱人就连谈话方式也与不太亲密的伴侣不同。他们可能有着双方都能明了的独特暗语和比喻用语，这样他们之间的沟通别人是听不懂的。他们会用到口头禅、特定的词汇或惯用语（idioms），只有他们自己才明白其中的含义，他们使用的惯用语越多，就越幸福美满（Dunleavy &amp; Booth-Butterfield，2009）。这样的人际交往与众不同，在研究中倾听伴侣谈话的陌生人都能分辨出谈话人是好友还是只是熟人（Planalp &amp; Benson，1992）。与一般伙伴的谈话相比，亲密伴侣之间的谈话显著特点是：清楚地了解对方，表现出更多的自我表露，令人更加放松。</p></div><h3 id="自我表露" tabindex="-1"><a class="header-anchor" href="#自我表露"><span>自我表露</span></a></h3><div class="hint-container note"><p class="hint-container-title">至此我们还没有发现男女两性的言语沟通存在巨大的差异。但这里有个差异很重要：在已确立的人际关系中，女性比男性表现出更多的自我表露，并且与她们在“开启者”量表（见专栏5.3）上的高分相一致，她们也能引导对方进行更多的自我表露（Dindia，2002）。的确，男性对女性伙伴进行的亲密自我表露一般多于对同性伙伴（如最要好的朋友）的表露——结果是有女人参与的人际交往通常比只有男性参与的交往更加亲密、更有意义（Reis，1998）。男性对女性较为开放，女性彼此之间也较为开放，但男性不怎么对其他男性进行自我表露。</p></div><div class="hint-container note"><p class="hint-container-title">这一切造成的重要后果是，男性常常依赖于从女性那里得到温情和亲密，反过来女性却较少依赖于男性（Wheeler et al.，1983）：女性可能与男女两性伙伴都有着亲密的、开放的和支持性的联系，而男性则可能只与女性共享最有意义的亲密情感。因而，男性一生中或许需要与女人交往来避免孤独，但在这方面女性通常并不需要男性。</p></div><p>### 工具性对表达性</p><div class="hint-container note"><p class="hint-container-title">女性之所以能与信赖的伴侣进行亲密的言语沟通，是因为她们往往有更高的表达性，注可以轻松自在地谈论自己的情感。具有高表达性的男性自然也能这样，正如双性化的男性就能像女性一样容易和两性都建立有意义、亲密的交往（Aubé et al.，1995）。所以有必要精确阐述一下上述观点，实际上只有传统的大男子主义的男性才会与最要好的朋友也只维持着表层肤浅的沟通，故而需要与女性建立亲密关系以避免孤独。大男子主义的男性也常把男性朋友拒之于千里之外（Shaffer et al.，1996），如果没有保持浪漫爱情关系的女性情侣，就往往会感到痛苦和孤独（Wheeler et al.，1983）。与此相反，双性化的男性（既自信果断又热情外向）能很轻松地向男性和女性进行自我表露，从而体验到与所有朋友交往的乐趣；结果他们不会感到孤独，与表达性低、传统型的男人相比，他们会花费更多的时间与女性交往（Reis，1986）</p></div><div class="hint-container note"><p class="hint-container-title">交谈反应性高的健谈女性和沉默寡言的男性在刚相遇的时候相处不错（Swann et al.，2006）——男方无需讲太多话语，因为女方一直在很快乐地说话——但如果他们想安顿下来在一起生活，这样的配对就存在危险（Swann et al.，2003）。出现冲突时，女方很可能会控制整个讨论的进行（她们一直就是这么做的；详见第11章），这种模式违反了男人是一家之主的传统社会期望。但这种模式并不会给开明的、双性化的男人造成什么麻烦，但却打击了传统型的大男子，因为后者如果和果断、健谈的女人配成双的话，从长期来看他们往往会变得很不满意（Angulo &amp; Swann，2007）。在讨论冲突时，丈夫比较沉默而妻子却很健谈，也会给旁观者留下糟糕的印象（Sellers et al.，2007），所以性别角色的刻板印象显然影响到我们关于异性交往的一些想当然的看法。</p></div><h3 id="精确表述" tabindex="-1"><a class="header-anchor" href="#精确表述"><span>精确表述</span></a></h3><div class="hint-container note"><p class="hint-container-title">注</p><p>把行为描述和第一人称陈述结合起来的简单方法是把它们整合成XYZ陈述（XYZ statement），从而更清楚而准确地进行沟通。XYZ陈述格式如下，“当你在Y情境下做X的时候” （标准的行为描述），“我感到Z”（第一人称陈述）。下一次听听你自己对伴侣的抱怨，你是在说：</p><p>“你怎么这么不为我着想！从来不让我把话说完！”</p><p>抑或，力求简明而准确地说出自己的意图：</p><p>“你刚刚打断我讲话的时候，我感到很生气。”</p><p>两者的效果差别很大。其中一个陈述有可能得到伴侣体贴的、表达歉意的回应，但另一个则可能适得其反。</p></div><h3 id="积极倾听" tabindex="-1"><a class="header-anchor" href="#积极倾听"><span>积极倾听</span></a></h3><div class="hint-container note"><p class="hint-container-title">在沟通中当我们接受到他人的信息时，有两个重要的任务要完成。第一是要准确地理解对方话语所表达的意思，第二是要向对方传达关注和理解，让他知道我们对他的话是在意的。这两个任务都可以通过复述（paraphrasing）接受到的信息而完成，即用自己的话重复对方的意思，让信息的传递者有机会肯定那就是他真正想要表达的意思。人们在谈话中用到复述时，并不想当然地认为自己理解了对方的话，并马上给予答复。相反，他们会复述对方的话并向对方重复地说出，从而花些时间来检查自己的理解是否正确。这看起来会使沟通不太流畅，但它确实是避免争吵和冲突的非常好的方法，不这样做就容易引起误解和错误。无论谈话在什么时候开始变得过激，复述都可以使之免于失控。</p></div><h3 id="守礼而镇定" tabindex="-1"><a class="header-anchor" href="#守礼而镇定"><span>守礼而镇定</span></a></h3><div class="hint-container note"><p class="hint-container-title">当然，人在被激怒的时候很难保持平静，镇定地思考。所以只要有可能就应该事先允诺与伴侣彼此以礼相待，这也是（试着）减少使人愤怒的事件的好方法（Gottman，1994b）。你或许愿意和伴侣定期地会谈，双方（礼貌地）表达各自的不满；如果你知道伴侣会设法解决你提出的问题，你在这周的其他时间里就都能轻松愉快地和伴侣相处（Markman et al.，1994）。在任何情况下，双方都不应该彼此来回反复地侮辱和讥讽。如果你发现自己处在这样一种消极情感相互作用的状况，可以暂停一下以打断这种恶性循环。要求休息片刻——“亲爱的，我太生气了，想不清楚。给我10分钟让我冷静一下”——等你不再那么激动的时候，再回来讨论刚才的问题（Markman et al.，1994）。独自一人待着，每分钟最多做6个深长的呼吸，你就能更快地平静下来（Tavris，1989）。</p></div><h2 id="第6章-相互依赖" tabindex="-1"><a class="header-anchor" href="#第6章-相互依赖"><span>第6章 相互依赖</span></a></h2><h3 id="人际关系的替代收益" tabindex="-1"><a class="header-anchor" href="#人际关系的替代收益"><span>人际关系的替代收益</span></a></h3><div class="hint-container note"><p class="hint-container-title">不过，相互依赖理论的另一个重要假设是，满意度并不是唯一的、甚至也不是决定亲密关系持续与否的最主要的影响因素。无论我们是否乐意，我们都会用到第二个标准，即替代的比较水平（comparison level for alternative，CLalt），来确定我们在其他的亲密关系中是否会更好。CLalt是指如果我们抛弃目前的亲密关系，而转投可以选择的更好的伴侣或情境，所能得到的交往结果。如果你是个聪明的会计师，就能明白CLalt就是我们可以容忍目前伴侣的最差结果。其原因在于：如果其他的亲密关系有希望得到比目前的关系更好的收益，即使我们对现状还满意，也有可能离开现在的伴侣去追求更大的收益。（记住，我们总在追求可能的最好结果。）另一方面，即使我们对自己目前的亲密关系不很满意，在没有更好的替代选择出现之前，我们也不可能脱离现在的关系。这个观点特别重要，可以解释为什么人们仍会待在使他们痛苦悲惨的亲密关系之中而不离去：尽管现实的处境非常痛苦，但置身其中的人们认为一旦离开情况会更糟。如果他们认为别处有更好的境况，就会选择离开（Edwards et al.，2011）。对亲密关系的满意程度并不是决定我们要保持或离开关系的主要因素，这一观点是相互依赖理论的最有趣最深刻的发现之一。</p></div><div class="hint-container note"><p class="hint-container-title">的确，只有在你意识到合意的替代伴侣时，你的CLalt才能提高。如果你对目前的伴侣感到满意，你或许不会太多注意到那些对你亲密伴侣具有强烈竞争力的对手。实际上，对现有亲密关系满意的人并不怎么有兴趣四处寻觅其他情侣。结果他们认为自己的CLalt比更关注替代伴侣的人低（Miller，2008）。这点可能很重要。留心自己的可选之人并密切关注替代恋人的大学生，相比于不太注意的大学生，会更频繁地更换自己的恋人（Miller，2008）。</p></div><div class="hint-container note"><p class="hint-container-title">正如我们将在第12 章中看到的，权力是影响他人行为的能力。社会交换的微妙理论之一较小利益原则（principle of lesser interest）认为，较少依赖于亲密关系的伴侣在关系中拥有更大的权力（Waller&amp; Hill，1951）。或者说，当理想的亲密关系结束时，失去较少的一方会居于主导地位。实际上，如果要赢得争执或达到自己的目的，这个原则就显得更加准确；一般双方都会承认，恋爱关系中更为独立的一方是两人中更有支配地位的人（Berman &amp;Bennett，1982）。</p></div><h3 id="与时俱变的比较水平" tabindex="-1"><a class="header-anchor" href="#与时俱变的比较水平"><span>与时俱变的比较水平</span></a></h3><div class="hint-container note"><p class="hint-container-title">注</p><p>设想你找到了完美的亲密关系。你的伴侣充满爱心、迷人、聪明、富有、慷慨、不知疲倦。同时还是特级厨师、娴熟的按摩师、汽车机械师。他/她提供你的关系结果超出了你最激情的梦想。每晚你回家的时候，伴侣为欢迎你例行地为你按摩、揉脚，还为你准备了精美的食物。你满意吗？当然满意。但在连续几个月都享受到这种幸福之后，可能会出现什么情况呢？</p><p>某晚你回到家时，因为爱人遇到交通堵塞而没有早回家，你没有了温柔的按摩，没有了美味的食物。“嘿，”你可能会想“我的美食呢？我的按摩呢？”你已经在期望这些美妙的待遇，这表明你的比较水平提高了。但当你的CL升高而关系结果维持不变，满意度就会下降（见本章下一节专栏6.2）。一旦你习惯于完美的伴侣，就会发现你从他/她的娇宠溺爱中得到的愉悦要比过去的少。</p><p>确实，相互依赖理论预测到这样的模式。因为比较水平是建立在我们过去经验的基础上，所以它往往会随着我们得到的关系结果而不断波动。当我们最初得到非常好的关系结果时，我们会非常愉悦，但随着我们开始把这些好处视为理所当然，CL开始升高时，我们的愉悦感会渐渐地减少。这样一来，即使没有任何改变（除了我们的期望），具有奖赏意义的亲密关系也会逐渐变得越来越不满意。</p></div><h3 id="亲密关系的经济论" tabindex="-1"><a class="header-anchor" href="#亲密关系的经济论"><span>亲密关系的经济论</span></a></h3><div class="hint-container note"><p class="hint-container-title">亲密关系的刻板印象是慷慨大度、充满爱心的，这种看法非常正确，彼此相敬如宾的伴侣比起那些彼此提供奖赏较少的伴侣更可能长相厮守（如Le et al.，2010）。</p></div><div class="hint-container note"><p class="hint-container-title">实际上，有研究比较了在问题解决的任务中，人们与自己的配偶及与完全陌生的人交往方式的差别（Vincent et al.，1975）。如果与自己不太了解的人讨论问题，参与者显得彬彬有礼、意气相投；他们会压抑批评、隐瞒异议并且掩盖自己的受挫感。相反，与伴侣在一起时，人们就会表现得比较令人讨厌。他们打断爱人的讲话，贬低其观点，公开唱反调。亲密和相互依赖看起来纵容了伴侣之间的无礼行为，而非表现出谦恭有礼、体贴周到。</p></div><div class="hint-container note"><p class="hint-container-title">举一个例子来说明上述观点。设想你在人行路上行走，一张20元的钞票吹到了你面前。旁边没有其他人，显然你可以把钱留下。拾到钱的感觉好吗？当然好了。现在再设想，你把20元放到了口袋里，再拿的时候钱不见了，只摸到一个破洞。这令你失望。但哪一个事件对你的情绪影响更强烈？得到意外之财还是丢失已有的钱？答案是：失去通常比同等程度的得到对我们的影响更大；我们喜欢得到，但我们更憎恨失去（Kahneman &amp; Tversky，1982）。</p></div><div class="hint-container note"><p class="hint-container-title">类似地，亲密关系中的讨厌事件比理论上同等程度的合意事件更引人注目、影响深远（Baumeister et al.，2001）。比如，某天晚上你在家，爱人分别表扬和批评了你一次，它们的效果并不会彼此抵消。表扬能够削弱批评带来的打击，但两者加在一起你还是会略微有点苦恼。坏的比好的更有力量。</p></div><h3 id="与时俱变的奖赏和代价" tabindex="-1"><a class="header-anchor" href="#与时俱变的奖赏和代价"><span>与时俱变的奖赏和代价</span></a></h3><div class="hint-container note"><p class="hint-container-title">为人父母这一事实就是个很好的例子。如果你还没有孩子，你或许想当然地认为做父母很有趣，自己的孩子一定会很可爱，养育孩子会让你和伴侣更加亲近。然而现实（如果你有孩子你会知道的）却是，“孩子出生后，婚姻关系将明确无误地走向黯淡”（ Stafford &amp; Dainton，1994，p.270）。我们可以有把握地说，为人父母是非同一般的、而且常常是奇迹般的探险历程，它毫无疑问严峻地考验着年轻父母之间的伴侣关系。养育孩子是件无休止的工作，大多数父母会发现，一起快乐相处的时间急剧而意外地减少了（Claxton &amp; Perry-Jenkins，2008）。当孩子出生，冲突会增多，对婚姻的满意度（以及对伴侣的爱）会减少（Doss et al.，2009），这在全世界都普遍（Wendorf et al.，2011）。如果年轻的父母没有预料到这些困难，将来肯定会大吃一惊。</p></div><h3 id="交换关系与共有关系" tabindex="-1"><a class="header-anchor" href="#交换关系与共有关系"><span>交换关系与共有关系</span></a></h3><div class="hint-container note"><p class="hint-container-title">在交换关系（exchange relationship）中，人们为他人付出期望得到同等的回报。如果他们得到别人的善意帮助，就觉得有义务回报别人，做到两不相欠。如表6.1所示，交换关系中的双方不喜欢欠对方人情；对共同做出的努力要分出各自的贡献；只有在认为自己有所得时才会关注对方的需要；即使拒绝帮助对方也不会感到内疚。正如你所预期到的，交换关系多为陌生人或一般熟人之间的关系，其典型的特征是肤浅、短暂、很大程度上是任务导向的。</p></div><div class="hint-container note"><p class="hint-container-title">相形之下，在共有关系（communal relationship）中，伴侣特别关注对方的幸福，彼此不期望任何回报地支持和关照对方（Beck &amp; Clark，2010b）。因此，寻求共有关系的人不会严格计算自己付出的代价，他们不会热切地希望自己的付出能立刻得到报答；即使没有机会得到好处，也会密切关注伴侣的需要；如果能帮到伴侣则自我感觉非常好。在共有的亲密关系中，人们常会为伴侣做出一些小牺牲，彼此帮对方的大忙，结果是他们享受到更高质量的亲密关系（Clark &amp; Grote，1998）。的确，人们喜欢这种关系的婚姻，伴侣彼此对对方表现的关注和慷慨越多，就越幸福（Clark et al.，2010），有价值的恋爱关系通常都是共有的亲密关系，但共有和交换这两个标准同样适用于友谊，朋友关系也有这两种类型（Clark &amp; Mills，1993）。</p></div><h3 id="多少才足够-公平对待与过度获益" tabindex="-1"><a class="header-anchor" href="#多少才足够-公平对待与过度获益"><span>多少才足够？公平对待与过度获益</span></a></h3><div class="hint-container note"><p class="hint-container-title">在家务劳动的分配和照料小孩这两个敏感问题上，保证公平是明智之举。如果这些杂务由双方平均分担，夫妻们往往都会对婚姻感到满意：“当平均分担了家务的重担，夫妻一方都可能会欣赏另一方的贡献，并且会有更多休闲时间来进行共同活动”（ Amato et al.，2007，p.166）。相形之下，如果伴侣一方承担大部分家务，“糟糕的情感就会肆虐，并影响到婚姻的质量” （ Amato et al.，2007，p.166）。不幸的是，已婚女性在这些家务的分配上很难得到公平对待（Feeney &amp; Noller，2002）；即使她们在家庭之外和丈夫有着相似的工作职责，职业女性往往承担着两倍于丈夫的家庭杂务（Davis et al.，2007）。同居的情侣、男同和女同通常在这些杂务的分担上更为公平（Coltrane &amp; Shih，2010），所以婚姻在某种程度上导致男人更少做家务。然而无论何时，这种不公平都会给亲密关系造成相当大的压力（Claffey &amp; Mickelson，2009）。的确，婚姻专家对当代夫妻的告诫一般是，“男人要多做家务、照料小孩、维系爱情，这样才能拥有幸福快乐的妻子”（ Gottman &amp; Carrère，1994，p.225）。在这些方面的公平比夫妻交往其他方面的公平更有影响。</p></div><h3 id="承诺的本质" tabindex="-1"><a class="header-anchor" href="#承诺的本质"><span>承诺的本质</span></a></h3><div class="hint-container note"><p class="hint-container-title">依恋和相互依赖</p><p>与之相反，安全型的人在共有情形下舒适自在，只要伴侣需要，他们就会提供关心和支持，他们乐于接纳伴侣对自己的依赖（Simpson et al.，2002）。有意思的是，我们越愿意接纳伴侣一时的贫弱无助，我们就越不会倾向于贫弱（Feeney，2007）。在这方面成人看上去更像孩童：如果他们知道自己随时可以得到安全和支持，比起支持不太确定的情况，他们的自主性和自律性更强。这就是为什么安全型的人总能找到心仪伴侣的另一个原因：他们能轻松地接纳亲密的相互依赖，这让伴侣更容易独立自主。</p></div><div class="hint-container note"><p class="hint-container-title">整体而言，投入模型认为在以下三种情况下人们希望与现有的伴侣保持亲密：感到幸福；没有其他更好的选择；离开的代价太高（见图6.8）。该模型假定这三个因素的影响同等重要，承诺就来自所有三个因素的复杂组合。所以，随着人们所处环境的变化：一方或双方伴侣都感到不满意，受到迷人的替代伴侣的诱惑，或者随时可以自由地离开关系，亲密关系常常要艰难地熬过这段时间。发生这样的插曲会使关系变得紧张，削弱伴侣的承诺，但如果承诺的其他构成成分能够将伴侣聚在一起，亲密关系就可能继续存在。</p></div><div class="hint-container note"><p class="hint-container-title">如果人们盲目地采取这些行为，常常会招致自我挫败。但是，如果在相互依赖的关系中这样做，而且伴侣双方都这样做，这类行为就能有力地保护亲密关系，并促进与他人的愉快交往（Ramirez，2008）。就这样，即使我们的内心根本上是贪婪的，但我们对好友和爱人常常是无私的、体贴的和关爱的。</p></div><h2 id="第7章-友谊" tabindex="-1"><a class="header-anchor" href="#第7章-友谊"><span>第7章 友谊</span></a></h2><div class="hint-container note"><p class="hint-container-title">那么，朋友关系是否和爱情关系一样，只不过亲密程度更低而已？答案既是肯定又是否定的。友谊和爱情关系的构成因素是一样的，但各个因素的结合不同。并且友谊通常也并不具备爱情的某些成分，所以两者的构成并不一样。但友谊和爱情的许多要素也非常类似，本章要详细描述亲密伙伴的特征，从而为爱情（第8章）的学习打下基础。我们还会涉及一些其他主题，比如友谊的各种特征，探讨男人和女人能否“仅仅做朋友”。</p></div><h3 id="友谊的属性" tabindex="-1"><a class="header-anchor" href="#友谊的属性"><span>友谊的属性</span></a></h3><div class="hint-container note"><p class="hint-container-title">人们在谈到朋友时会想起各种各样的特征（Fuhrman et al.，2009；Hall，2009）。首先，亲密的朋友能感受到彼此间的温情。他们彼此欣赏、信任和尊重，珍视忠诚与真诚，双方都能无拘无束地做真实的自己，根本不用娇柔做作。其次，友谊涉及共同参与和分享。朋友之间反复进行有意义的自我表露，提供情感支持和实际援助，遵守平等规范，双方的喜好都受到应有的重视。最后，朋友还能彼此相伴。他们分享兴趣，参与活动，认为彼此才是娱乐和搞笑的源泉。最好的友谊显然是一种紧密、有奖赏价值的关系，故而有学者把友谊（friendship）定义为“一种自发的人际关系，通常表现出亲密和扶助，双方彼此欣赏，并企求对方的陪伴”（Fehr，1996，p7）。</p></div><h3 id="友谊和爱情的差异" tabindex="-1"><a class="header-anchor" href="#友谊和爱情的差异"><span>友谊和爱情的差异</span></a></h3><div class="hint-container note"><p class="hint-container-title">那么，怎么区分友谊和爱情？我们在第8章详细讨论爱情时将会看到，爱恋包含着比喜欢更复杂的情感。喜欢和爱恋都包含对伙伴正面、热情的评价，但浪漫的爱情还包括对伴侣的着迷、性的欲望和比友谊强烈得多的排他性的愿望（Giordano et al.，2006）。爱情关系还有着更严厉的行为标准；对待爱人应该比对朋友更加忠诚、更加愿意伸出援手（Fuhrman et al.，2009）。调节友谊的社会规范较之爱情关系的规范约束力较小，友谊也更易于解体（Fehr，1996）。此外，朋友不太可能会公开表达正面情感，他们在一起相处的空闲时间一般也比情侣要少得多。</p></div><p>### 社会支持</p><div class="hint-container note"><p class="hint-container-title">朋友远比我们想象的更重要</p><p>你可能认识到亲密的友谊所带来的快乐，但朋友对你的影响甚至超出你的想象。朋友往往会影响我们爱情关系的走向。他们通常把我们介绍给可能的对象，并为此奔波忙碌，以帮助新恋情发展（Ackerman&amp; Kenrick，2009）。之后，他们对我们发展中的恋情会给出褒贬意见，其看法有一定的作用（Etcheverry et al.，2008）。如果朋友不赞成你们的恋情，恋爱关系会面临危险：即使年轻的恋人们（最初）对自己的关系满意，如果他们的朋友不赞成这段恋爱关系，7 个月之后恋人们更可能分手（Lehmiller &amp; Agnew，2007）。</p><p>朋友甚至还对我们快乐与否（或胖瘦与否！）都有着惊人的影响力！一项历时30 年的健康研究考察了12 000 多个人，发现有快乐朋友的人更可能感到快乐（Christakis &amp; Fowler，2009）。我们每拥有一个快乐的朋友，自己快乐的机会就会增加15%。我们朋友的朋友也很重要；我们的朋友每拥有一个快乐的朋友，我们快乐的机会就会增加10%，即使我们从未与此人谋面！我们社交网络所支持的规范及提供的经验非常强大，对我们有利也有弊。比如，如果有朋友变得肥胖，你超重的概率会增加57%。我们每拥有一个不快乐的朋友，自己快乐的可能性就会减少7%。孤独更具传染性：如果有朋友先变得孤独，我们变孤独的可能性会增加52%，如果朋友的朋友变得孤独则会增加25%（Cacioppo et al.，2009）。我们与他人的联系通常远超我们的认识程度，朋友往往远比我们想象得更重要。</p></div><div class="hint-container note"><p class="hint-container-title">进而，最好的支持应该切合我们的需要和偏好。并非所有的社会支持都完全对受援者有益。即使提供支持的朋友动机良善，具有利他精神，他们的支持也可能不对路或者太过殷勤（Brock &amp; Lawrence，2009）；帮助行为可能威胁到我们的自尊或者显得冒失，而我们接受这类帮助时会讨厌这种受惠或蒙恩的感觉（Gleason et al.，2008）。所以，社会帮助有时候会伴随有情感的代价，因此最好的援助有时或许是无形的支持（invisible support），悄然施行而不为受援者所察觉（Howland &amp; Simpson，2010）。当同居的情侣有一方正在准备紧张的律师资格考试时，双方把各自给予和得到的支持都记录下来，结果发现最能减少应试者焦虑的，是情侣提供而应试者并未觉察到的帮助（Bolger et al.，2000）。有时候，帮助朋友最好的方法就是默默地提供支持，从而不会增添他/她的心理负担。</p></div><h3 id="应答性" tabindex="-1"><a class="header-anchor" href="#应答性"><span>应答性</span></a></h3><div class="hint-container note"><p class="hint-container-title">上述亲密友谊的每种特征（尊重、信任、资本化和社会支持）让我们感到有人理解、关心和珍视自己，因而它们都与有奖赏价值的亲密关系最后一个部分有关系，这部分可能是亲密友谊中最重要的成分（Reis，2009）：应答性（responsiveness），即在认知层面上对朋友的需要和兴趣表现出关注与支持。大多数时间，朋友对我们是谁，我们要说什么都会有兴趣。他们关注我们，藉此表达他们珍视与我们的朋友关系。他们通常还表现出热情和支持，看来理解和欣赏我们。这些都是彼此能成为朋友的原因。认为某人对你的需要和欲望表现出关注、尊重、热心和支持，这种判断就是所谓的感知到的伴侣应答性（perceived partner responsiveness），它非常具有奖赏价值，注那些能让我们感受到自己有价值、理解和尊重的人都会吸引我们。</p></div><div class="hint-container note"><p class="hint-container-title">什么样的人才是最好的朋友？</p><p>人们往往会认识很多友好的熟人、一些随意交往的朋友、数个亲密的朋友，但只有一、两个能称之为关系最好的朋友。最好的朋友有什么特别之处？最好的朋友与我们生命中其他重要的人有什么区别？</p><p>简单的回答就是，这完全是个程度问题（Fehr，1996）。最好的友谊比普通的友谊更加亲密，包含亲密关系的所有构成成分（见第1 章）。考虑下了解程度：最好的朋友通常是我们最亲近的知己。他们常常知道我们的秘密，而这些秘密是其他任何人都不了解的，甚至包括我们的配偶！考虑下信任程度：我们一般期望能从最好的朋友那里获得最大的支持，所以最好的朋友就是“不管怎样都为你守候的人”（Yager，1997，p.18）。考虑下相互依赖性：当我们最好的朋友就在身边并且能够接近，我们就会设法探望他们，次数要远多于探望其他的朋友；与他们交往的频次和情境范围也远超不太亲密的朋友。最后考虑下忠诚度：我们通常期望最好的朋友是永远的好友。因为这样的人“是所有朋友中独一无二的”，最好的友谊一贯能经受“时间和困难的检验，比如搬迁等重大变化，比如结婚和生养小孩等身份地位的改变”（ Yager，1997，p.18）。</p><p>总而言之，最好的友谊并非某种截然不同的独特人际关系类型（Fehr，1996）。相反， 只不过比其他友谊更加亲密而已——涉及更丰富、更有奖赏价值和更多的人际接触——这就是它们为人所珍视的原因。</p></div><h3 id="中年期" tabindex="-1"><a class="header-anchor" href="#中年期"><span>中年期</span></a></h3><div class="hint-container note"><p class="hint-container-title">当人们和爱侣安居后友谊又会怎样变化？友谊和爱情之间的关联非常清晰：当人们有了恋人时，他们与家人和朋友相处的时间会减少。这时会发生二元退缩（dyadic withdrawal）现象：人们与爱人见面的次数越来越多，而探望朋友的次数越来越少（Fehr，1999）。一项研究发现，当人们还是偶尔与恋人约会时，平均每天和好友相处的时间是两小时，但一旦与恋人订婚后每天探望朋友的时间还不到30分钟（Milardo et al.，1983）。相爱的情侣确实会与具有共同特点的朋友有着更多的接触，但这并不能抵消他们朋友总数的下降以及与朋友相处时间的减少。</p></div><div class="hint-container note"><p class="hint-container-title">人们友谊的消退在结婚之后仍不会停止。与异性朋友的友谊尤其会受到影响；异性朋友可能会被配偶另一方认为是潜在的情敌，所以已婚人士一般很少探望异性朋友（Werking，1997）。尽管如此，即使夫妻们很少探望他们的朋友，他们仍比单身的时候有着更大的社交网络，因为他们会更多地看望他们的姻亲（Milardo et al.，1983）。（请不要误会这一点，如果你不喜欢爱人一方的家庭成员也请当心：一旦你结婚了将会更频繁地看到他们！）</p></div><div class="hint-container note"><p class="hint-container-title">因而，当人们投身于配偶和孩子时，他们的社会生活并不会完全萎缩破灭，但他们社交活动的重心的确从私人朋友转移到家庭和夫妻共同的朋友上。事实上，如果夫妻双方没有共同的朋友，这样的婚姻通常很艰难。正如图7.1所示，如果夫妻双方都没有一个共同的朋友，配偶们就会面临更多的婚姻问题（Amato et al.，2007）。已婚人士有自己的私人朋友没什么坏处，但如果只有独享式的友谊似乎就有风险。</p></div><h3 id="老年期" tabindex="-1"><a class="header-anchor" href="#老年期"><span>老年期</span></a></h3><div class="hint-container note"><p class="hint-container-title">社会情绪选择理论（socioemotional selectivity theory）认为，友谊随年龄的这种变化是因为老年人有着与年轻人不同的人际目标（Lockenhoff &amp; Carstensen，2004）。年轻人的人生道路还很漫长，所以该理论假定他们会追求旨在获取信息、指向未来的目标，这有利于他们今后的生活。（这大概正是当代大学生们的社交活动的特征。）心中带着这样的社交目的，年轻人会追求各种类型的社交伙伴和相对更大的社交网络。然而，当人们变老，未来显得越来越有限，他们的社交目标大概更多地指向现在而不是未来，也更注重最大限度地满足自己的情感需要（Fung &amp; Carstensen，2004）。社会情绪选择理论的观点认为，随着有生之年变得越来越屈指可数，老年人交际的目标指向友谊的质量而非数量；他们更注重精选出的深厚友情，不愿发生冲突（Fingerman &amp; Charles，2010），而让更普通的朋友关系自然消失。的确，该理论预测：任何人，只要认为自己的未来不长久，就会花更多的时间与少数几个亲密好友相处，而不是把时间用来和各种各样的普通朋友交往——这也是年轻人感染艾滋病病毒后，所剩时日无多时与人交往的情形（Carstensen et al.，1999）。一般而言，社会情绪选择理论看来是对与衰老有关的社交活动变化的合理解释。</p></div><h3 id="同性友谊中的性别差异" tabindex="-1"><a class="header-anchor" href="#同性友谊中的性别差异"><span>同性友谊中的性别差异</span></a></h3><div class="hint-container note"><p class="hint-container-title">大量研究表明女性之间的友谊是以情感分享（emotional sharing）和自我表露为其特征的，而男性友谊则是围绕着共同活动（shared activities）、相伴相随和搞笑娱乐而展开的（Fehr，1996；Marshall，2010）。这显得过于简单化，但多年前赖特（Wright，1982）创造出的两个精辟词语今天看来仍熠熠生辉：女性的友谊是“面对面”（face-to-face），而男性的友谊则是“肩并肩”（side-by-side）。</p></div><div class="hint-container note"><p class="hint-container-title">男人和女人能成为密友吗？</p><p>当然，男人和女人常常能成为异性密友。大多数人都曾和许多异性有过亲密的友谊，这种朋友关系在大学生之间更是司空见惯。然而一旦他们离开校园，大多数人不再会维持亲密的异性友谊（Marshall，2010）。为什么？究竟发生了什么变故？</p><p>第一点值得注意的是，男女两性成为朋友的原因和他们接近同性朋友的原因一样；同样涉及应答性、信任和社会支持（Fuhrman et al.，2009）。因为异性友谊中，男性交往的对象是女性，所以他们与女性朋友相处比与男性伙伴在一起时，显得更加开放和具有表达能力（Fehr，1996）。的确，具有高表达性的男性以及具有高工具性的女性，比他们的同辈更有可能与异性建立亲密的友谊（Lenton &amp; Webber，2006）。一如既往，相像才能相吸。</p><p>然而，异性友谊还会遭逢同性友谊通常不会遇到的障碍：必须做出选择，是继续保持纯洁的异性友谊呢？还是转变为爱情？友谊通常没有排他性，没有性含义，是平等的伙伴关系，当人们试图和异性建立亲密的友谊时，就会发现自己置身于完全陌生的领域（Werking，1997）。异性友谊的重大问题是朋友之间是否——毕竟彼此非常亲密——会发生性关系。男性比女性更可能认为性事是个好主意（Lehmiller et al.，2011），男性认为女性朋友对性事的兴趣往往会超出实际（Koenig et al.，2007）。而女性一方通常会低估男性朋友想和她上床的意愿程度，所以经常会发生一些误会：“大多数女性并不会回应男性朋友的性渴望，尽管有时男性会自我欺骗地认为女性朋友会在性事上报答自己”（Buss，2003，p.262）。因此，在一项调查中有五分之一的女性和四分之一的男性报告，他们最讨厌异性友谊中的“性紧张”（Marshall，2010）。</p><p>另一方面，性挑逗有时又是人们最喜欢异性友谊的方面，在一项研究中60%的年轻成人报告，他们至少和一位并非恋人的朋友有过性关系（Bisson &amp; Levine，2009）。大多数情形下，异性朋友认为性事能改善他们之间的关系，使他们彼此更加亲近，而不会从朋友变成恋人。因而，他们变成了“发生性关系的朋友”（Owen&amp; Fincham，2011）。</p><p>尽管如此，朋友之间发生性关系总会带来很多问题；它会引起混乱并使人们的关系变得复杂，大多数的异性友谊从来不会带有性的色彩（Halatsis &amp; Christakis，2009）。的确，多数情况下人们不会把异性友谊转变为爱情，他们实际上会尽力保持柏拉图式的伙伴关系。之所以这样做有几个原因（Messman et al.，2000）。有时人们不愿意冒着失去有价值的人际关系的风险，而把异性友谊转变为爱情。正如一项研究中参与者所报告的（Werking，1p.102）：“我真的喜欢与她的友谊。如果我们能成为恋人，这固然很好。但那样我可能会失去宝贵的友谊。”其他原因还包括缺少性的欲火，没有足够强的意愿，也可能存在持反对意见的第三方（比如恋人）。</p><p>的确，如果异性朋友和其他人结了婚，那么他们的异性友谊很难处理。大概看来，在婚姻之外能提供伴护和关爱的异性朋友，能减少配偶一方的情感负担（Werking，1997）。然而，即使没有牵涉到性事，夫妻一方与潜在情敌的亲密联系都可能对另一方构成威胁（我将在第10 章详细阐述）。因此，已婚的夫妇不太可能像单身男女那样拥有亲密的异性友谊，这正是大学生在完成学业之后异性友谊不再那么普遍的重要原因。</p></div><h3 id="孤独" tabindex="-1"><a class="header-anchor" href="#孤独"><span>孤独</span></a></h3><div class="hint-container note"><p class="hint-container-title">在社交场景中我们有时会感到焦虑和胆怯，有时还会因为没有亲密朋友而感到不满和苦恼，这两种社交体验并不一样。如果我们所期待的人际关系数量和质量与我们当前实际情形存在较大差距，就会产生令人不悦的厌烦、悲伤和绝望的孤独体验（de Jong Gierveld et al.，2006）。孤独与独处并不是一回事；在完全独处的状态我们通常也能感到满足（Leary et al.，2003）。相反，当我们想拥有与他人更多、更亲密的接触而不得时，就会产生孤独感（Mellor et al.，2008）。因此，如果你与他人的人际关系很淡漠，即使你身边有很多人或者你在脸谱网上有许多“朋友”，你仍可能会感到孤独。</p></div><div class="hint-container note"><p class="hint-container-title">而且，平均而言男性比女性更孤独（Pinquart，2003），但更多地取决于他们当前是否能与女性伙伴保持亲近。因为女性常常与其他女性有着亲密的友谊，即使她们没有任何约会情人，她们在生活中通常也能与其他女人分享亲密的情感。男性则相反，他们与其他男性只能保持相对肤浅的人际交往，一般只有在与女性交往时，他们才能真正敞开心扉。因此，男性似乎依赖于女性以避免孤独，而女性则不会反过来依赖男性避免孤独（如表7.5所示）。</p></div><h2 id="第8章-爱情" tabindex="-1"><a class="header-anchor" href="#第8章-爱情"><span>第8章 爱情</span></a></h2><h3 id="爱情三角理论" tabindex="-1"><a class="header-anchor" href="#爱情三角理论"><span>爱情三角理论</span></a></h3><div class="hint-container note"><p class="hint-container-title">罗伯特·斯腾伯格（Robert Sternberg）认为各种不同的爱情都能由三个构成成分组合而成（Sternberg，1987，2006）。爱情的第一个成分是亲密（intimacy），包括热情、理解、沟通、支持和分享等爱情关系中常见的特征。第二个成分是激情（passion），其主要特征为性的唤醒和欲望。激情常以性渴望的形式出现，但任何能使伴侣感到满足的强烈情感需要都可以归入此类。爱情的最后一个成分是承诺（commitment），指投身于爱情和努力维护爱情的决心。承诺在本质上主要是认知性的，而亲密是情感性的，激情则是一种动机或者驱力。恋爱关系的“火热”来自激情，温情来自亲密；相形之下，承诺反映的则是完全与情感或性情无关的决策。</p></div><p>### 唤醒</p><div class="hint-container note"><p class="hint-container-title">总而言之，这些研究表明肾上腺素增强了人们的爱情体验。不同类型的高唤醒，包括简单的体力活动和恐惧、厌恶、快乐等情绪状态，似乎都可以提高我们对合意伴侣所感受到的浪漫吸引力。请思考一下它对于我们生活的意义：你是否曾经与爱人大声争吵，而几分钟之后“亲吻、和解”会显得格外亲密？或许你的愤怒增加了随后的激情？这是否就是“恋爱中”的感觉？</p></div><h3 id="思维" tabindex="-1"><a class="header-anchor" href="#思维"><span>思维</span></a></h3><div class="hint-container note"><p class="hint-container-title">此类研究结果都表明，现实中“爱情是盲目的”：人们会低估或忽视爱侣的缺点。他们将爱侣的形象理想化，这与他们面对的具体事实有着重大差别。事实上，爱情和友谊的主要差别在于我们的想象——爱侣是迷人、神秘而且有吸引力的，而朋友则不是这样（Aron et al.，2008）。</p></div><h3 id="生理基础" tabindex="-1"><a class="header-anchor" href="#生理基础"><span>生理基础</span></a></h3><div class="hint-container note"><p class="hint-container-title">相伴之爱还与浪漫之爱具有不同的生理基础。促进人放松并减少压力的神经肽催产素好像与相伴之爱有关（MacDonald &amp; MacDonald，2010）。处在分娩和哺乳期的妈妈会分泌催产素（事实上，催产素的合成制剂（pitocin）常用来诱导分娩），年轻妈妈血液中催产素含量越多，就越会搂抱婴儿、与婴儿喃喃细语、照看婴儿并微笑（Feldman et al.，2007）。对于成人，爱人的接吻会刺激催产素的释放（Hill et al.，2009），尤其在性高潮时会释放大量的催产素；催产素可能正是夫妻在做爱之后会感到放松和嗜睡的原因之一（Floyd，2006）。而且，血液中催产素含量高的人在与配偶讨论棘手问题时往往显得更温和、更善良（Gouin et al.，2010），实验参与者在与恋人争吵时闻到催产素喷剂后会开始变得更放松（Ditzen et al.，2009）。他们还变得对陌生人更为信任（Theodoridou et al.，2009）。这些现象表明催产素能产生令人安宁的宽慰作用，还能使有血缘关系的人之间彼此更加依恋（Floyd，2006）。简而言之，催产素的释放或许是相伴之爱产生的生物基础。</p></div><h3 id="浪漫爱情为何难以持久" tabindex="-1"><a class="header-anchor" href="#浪漫爱情为何难以持久"><span>浪漫爱情为何难以持久</span></a></h3><div class="hint-container note"><p class="hint-container-title">事实上，如果我们仔细考虑，就会发现浪漫的爱情之所以会随着时间而减弱有几个原因（Walster &amp; Walster，1978）：首先，幻想促进了浪漫。如前所述，爱情在一定程度上是盲目的。洋溢着激情的爱人们往往会将他们的伴侣理想化，缩小或忽略那些使他们止步不前的不利信息。想象、希望和异想天开的幻想会使与我们差异很大的人看起来有吸引力，至少暂时是这样。当然，问题在于，幻想会随着时间的流逝和经验的积累而逐渐变弱。伴侣之间理想化的赞美能促进浪漫，仅就这一点足以预期：当伴侣双方开始生活在一起、变得越来越现实时，浪漫就会消退。“理想容易黯淡，魔咒会被打破，戏法终将被戳穿……浪漫随着时间推移而逐渐消逝，因为熟悉能使人更现实、毫无保留地审视对方；晌午刺眼的阳光最终驱散了月亮留下的迷人光晕”（ Mitchell，2002，p.94）。</p></div><div class="hint-container note"><p class="hint-container-title">此外，仅仅是新奇也能为新确立的爱情关系注入兴奋和能量。恋人之间的初吻比之后的成千上万个亲吻更令人激动，而当人们为新的伴侣而精神抖擞、魂牵梦绕时，决不会意识到在30年之后自己的爱人会变得多么熟悉和习以为常。的确，新奇在其他物种中也会引起性唤醒。</p></div><div class="hint-container note"><p class="hint-container-title">最后，图8.3也表明，唤醒随着时间的流逝会逐渐消失。我们都清楚，身体的唤醒——如脉搏加快、呼吸急促——无疑会增强激情。但人们不可能永远保持紧张的激动状态！就浪漫的爱情而言，当伴侣变得熟悉时大脑可能根本无法产生足够多的多巴胺，所以即使你的伴侣能一如既往地完美，你也不能像过去那样被唤醒。无论如何，不管何种原因，爱情的激情成分比亲密和承诺会更快地发生变化（Ahmetoglu et al.，2010），这就意味着浪漫的爱情也会发生改变。</p></div><h2 id="第9章-性爱" tabindex="-1"><a class="header-anchor" href="#第9章-性爱"><span>第9章 性爱</span></a></h2><h3 id="对随意性行为的态度" tabindex="-1"><a class="header-anchor" href="#对随意性行为的态度"><span>对随意性行为的态度</span></a></h3><div class="hint-container note"><p class="hint-container-title">男性和女性的性观念是否存在差异？平均来看，的确存在差异：男性在性的价值取向和态度体验上更为宽容，虽然随着时代的进步这种差异会越来越小，但差别大小取决于所要测量的具体态度（Petersen &amp; Hyde，2010）。男女两性在性观念上的最大的差异是对待婚前随意性行为的态度；男性比女性更可能认为没有爱情的性也可接受。这种差异毫无疑问会影响男女两性对过去性行为的反思：女性更可能后悔自己过去的行为（如发生一夜情），而男性更可能悔不当初（如有人追求自己时没有发生性关系）。谈到随意性行为时，女性往往后悔自己的行为，而男性则后悔自己没有行动（Galperin et al.，2011）。</p></div><div class="hint-container note"><p class="hint-container-title">个体的性可能还涉及其他性态度。传统上，人们对女性性经历或性宽容的评价要比男性更为严厉。男性如果有许多性伙伴，会被人称赞为“风流帅哥”，而女性如果有同样数量的性伙伴，则会被人贬斥为“淫荡骚货”。这种不一致就是性的双重标准，它在几年前还非常明显地存在，但今天看来却更为微妙（Crawford &amp; Popp，2003）。如今，挑逗别人并发生性关系的女性，比发生类似行为的男性所受到的评价更为宽容；人们认为前者较后者更热情、更安全、床上表现更好（Conley，2011）。并且，乱交的男女都会受到他人负面的评价（Marks &amp; Fraley，2005）。然而男性和女性如果同样身患性传播疾病，人们对女性的评价远比对男性的评价更为苛刻严厉（Smith et al.，2008），在参与3P的男女中，女性得到的评价更差（Jonason &amp; Marks，2009）。注所以，明显的性双重标准貌似不再存在，但个体的性行为仍然会影响人们对其性经历的评价。</p></div><div class="hint-container note"><p class="hint-container-title">同性恋是否有演化根源？</p><p>性取向具有基因基础（Lǎngström et al.，2010），但男同和女同自己的孩子一般并不多，那么，在以异性恋为主体的人口中，性选择的演化原理又是如何发挥作用以维持少量而持续存在的同性恋们？原因可能有很多，这里给出两个来自男同研究的结果。首先，男同都是很好的伯/叔祖父；他们对外甥或侄女辈的付出比其他人更多，藉此能帮助他们的手足兄弟更成功地生育后代（Vasey &amp; VanderLaan，2010）。 其次，男同的姐妹们与其他女人相比生殖能力更强，有更多的孩子（Iemmola &amp; Ciani，2009）。因此，长期来看男同很少生养他们自己的后代，但他们的姐妹们却有更多的孩子，而且总的来看，这些孩子能得到更好的照料和保护。一般而言，在生存困难的环境下，家族中存在着同性取向甚至是一种优势。上述两种情况就可能是同性取向的演化根源。下次如果你听到性狭隘者声称：男同与女同不可能是天生的， 因为他们没有孩子，请自信地用本专栏反驳他们。</p></div><h3 id="第一次性行为" tabindex="-1"><a class="header-anchor" href="#第一次性行为"><span>第一次性行为</span></a></h3><div class="hint-container note"><p class="hint-container-title">第二，这里出现了另一个构筑人际关系科学的必要道德前提：如果对人类同伴关系所进行的冷静、细致的研究能发现可靠的知识，促进我们的健康和幸福，我们就应该进行这些研究，即使它们会把我们带入敏感的领域。只要一谈到性行为，有些人就仍然认为无知就是福，但这种观点是人际关系学家所坚定反对的。</p></div><div class="hint-container note"><p class="hint-container-title">人们性行为的次数受到其亲密关系性质（和持续期间）的影响。同居的情侣平均每周会有3次性行为，而已婚的夫妻每周的性行为约为2次（Willetts et al.，2004）。然而，这两种关系下的伴侣性行为的次数都多于那些单身人士（Smith，2006），这可能是因为单身人士不太可能拥有稳定的性伴侣。已婚人士有时或许会羡慕单身人士性放任的生活，但他们性行为的次数通常比单身人士更多。</p></div><h3 id="不贞" tabindex="-1"><a class="header-anchor" href="#不贞"><span>不贞</span></a></h3><div class="hint-container note"><p class="hint-container-title">只有在承诺和充满深情的亲密关系背景下才愿意发生性行为的人就具有“保守的”社会性性行为取向，而那些在追求性行为时并不寻求足够的亲密和承诺的人就具有“开放的”性行为取向。具有开放的性行为取向的人往往精力充沛、喜欢调情卖俏，总是在寻觅猎取新的性伙伴（Simpson et al.，2004）。他们喜好交际、外向开朗，千杯不醉（Clark，2004）。在全世界，平均来看男性比女性更为开放（Schmitt，2005b）。</p></div><div class="hint-container note"><p class="hint-container-title">然而，有趣的是，如果把这些人的面孔照片并排呈现，观察者一般都能分辨性行为取向开放者（他们看上去就在猎色）和性行为取向保守者（他们显得更为忠贞）（Boothroyd et al.，2011）。性开放女人的面部特征比保守的女人总显得有些男性化，更明显的是，虽然她们更吸引人，但男人认为她们并不适合做长期的伴侣。她们显得可爱，但与其他女人相比似乎不值得信赖（Campbell et al.，2009）。性开放的男人看上去也很有阳刚之气，但女人更喜欢性保守男人的面孔，认为他们更适合做长期的伴侣（Boothroyd et al.，2008）；她们似乎意识到开放的男人将来都会成为危险的丈夫。</p></div><div class="hint-container note"><p class="hint-container-title">演化学理论对所有这些现象给出了有趣的诠释。男性有着较低的养育投入，注他们能负担得起相对随意的性行为，并且可以认为，在人类漫长的演化历史进程中，性选择注偏利于那些尽可能多地与女性交配的男性。可是为什么演化也支持女性背叛欺骗自己的爱人？考虑到女性不贞的行为如果败露，可能要付出惨重的代价（Kaighobadi et al.，2008），这样还会有生殖优势吗？有争议性的回答是，女性通过伴侣外性行为并不能生育更多的孩子，但她可能更优秀地生育孩子（也就是说更健康、更俊美的后代）。优质基因假说（good genes hypothesis）认为某些女性——尤其是那些配偶不太合意的女性——能从双重交配策略中获益，通过这种策略她们（1）能追求到长期的伴侣，为她们提供保护和喂养后代的资源，同时（2）暗中从其他男性那里为她们的后代寻求更好的基因（Pillsworth &amp; Haselton，2006）。从某位男性那里得到承诺和安全，而和另一位男性孕育更高挑、更强壮、更健康的孩子，女性生育的后代就特别可能存活下去并能茁壮成长。</p></div><div class="hint-container note"><p class="hint-container-title">这些结果表明，在遥远的过去男性偶尔会碰到精子竞争（sperm competition）的情形，当两个或更多男性的精子同时充溢着一位女性的阴道时就会产生竞争（Shackelford &amp; Goetz，2007）。有研究者声称为了应对这种情况，长期演化使得男性的阴茎具备了最完美形状，从而能把其他男人的任何精子从他们伴侣的子宫颈排挤出去（Gallup &amp; Burch，2006）。根据常识人们会认为，第二位性交的男性只会把先前男性的精液从子宫颈推进到女性的子宫里，但实际情形并非如此；阴茎纵深的抽插会把任何已经出现在阴道里的精子挤压到龟头后方，随后把精子带出女性的体内。的确，与这种看法一致的是，如果女伴把男性置于精子竞争的风险之中，他们往往会表现出特定的性交方式——在较长的时间阶段里出现更多次非常深的插入——这特别有可能替换掉先前其他人可能留下的精子（Goetz et al.，2005）。</p></div><h3 id="性欲望" tabindex="-1"><a class="header-anchor" href="#性欲望"><span>性欲望</span></a></h3><div class="hint-container note"><p class="hint-container-title">注</p><p>男性社会性性行为指数得分高和不贞行为的多发，或许（部分地）是由另一个更大的两性差异所引起。平均而言，男性比女性有着更高的性驱力（sex drive）。他们比女性体验到更频繁、更强烈的性欲望，通常他们的性活动有着更强烈的动机激励（Vohs et al.，2004）。对年轻人的研究发现，男性每周会体验到37次性欲望的经历，而女性体验到的只有9次（Regan &amp; Atkins，2006）。由于你现在已经是明智的关系科学爱好者，请记住在男性之间和女性之间存在着相当大的个体差异，并且肯定有很多男性长期来看并不如某些女性那样好色。尽管如此，大量事实表明在全世界男性整体上比女性有着更高的性驱力（Lippa，2009）：</p><ul><li><p>男性更经常手淫。有着固定性伙伴的男性几乎有一半的人仍然每周手淫一次以上，而置身于性关系中的女性只有16%的人这样频繁地手淫（Klusmann，2002）。在英格兰，16~44岁的男子中73%的人在上一个月手淫过，而女子只有37%（Gerressu et al.，2008）。</p></li><li><p>男性比女性更经常地渴望性生活，在性行为次数一样的情形下，男性比女性更有可能感到不满足（Sprecher，2002）。</p></li><li><p>在亲密关系发展的过程中，男性希望开始发生性行为的时间一般要早于女性（Sprecher et al.，1995）。结果，女性通常是新的亲密关系中决定何时开始性行为的“看门人”。通常来看，当他首先想要发生性行为时，他必须等待，但当她想要发生性行为时，他们就能进行。</p></li><li><p>男性比女性更经常地进行性幻想（Leitenberg &amp; Henning，1995）。男性每周约有60次想到性行为；是女性只有15次（Regan &amp; Atkins，2006）。</p></li><li><p>男性在性上花费更多的金钱，购买更多的性玩具和色情素材（Laumann et al.，1994）。具体来说，男性有时会付费以获得性交——澳大利亚的一项研究发现，23%的男性至少有一次为性行为付过款——但女性几乎从来不会这样做（Pitts et al.，2004）。</p></li><li><p>最后，我们曾经提到过，男性平均比女性更能接受随意的性行为（Ostovich &amp; Sabini，2004）。他们也愿意与更多的人发生性行为（McBurney et al.，2005）。</p></li></ul></div><div class="hint-container note"><p class="hint-container-title">这点看上去粗鄙不堪，但情侣们无需介意于这一现象，以免影响他们的交往。相反，人们总是想当然地认为，女性如果长期接受男性许多的礼遇——比如昂贵的约会和其他令人满意的招待——就应该有义务使他们关系更加亲密（否则她就应该停止接受这些礼遇）。咨询专栏作家认可了这一点：“女性并不认为免费的宴请就应以性来报答，但承担所有约会开销的男性却认为女性对浪漫的、最终亲密的关系感兴趣。否则他们就会觉得被人利用并痛恨此事”（Mitchell &amp; Sugar，2008，p.B2）。这一模式的消极后果是，当男性感到女性“亏欠”他们时，就会理直气壮地施加压力或强制女性发生性行为（Basow &amp; Minieri，2011）。</p></div><h3 id="性沟通" tabindex="-1"><a class="header-anchor" href="#性沟通"><span>性沟通</span></a></h3><div class="hint-container note"><p class="hint-container-title">研究者观察了伴侣们的性行为，并广泛地对他们进行了访谈，结果发现男同和女同们性经历的主观质量——包括心理上的投入、对伴侣的需要和愿望的应答性、对性经历每个方面的享受——实际上要好于异性恋者。同性性行为是更满意的性行为。男同和女同性互动的优势之一是参与者双方都是同性；男同和女同都知道他们自己的喜好，也能合理地预测他们伴侣的喜好。然而，马斯特斯和约翰逊认为有价值的同性关系的首要基础就是良好的沟通。男同和女同能比异性恋者更轻松、更坦率地谈论他们的性感受。他们会彼此询问双方所期望的动作，感觉良好时会给出反馈，并且一般会指导自己的爱人怎样取悦自己。相形之下，异性恋伴侣则会“持续忽视”公开的性沟通并显示出“对伴侣缺乏好奇心”的自我毁灭倾向（Masters &amp; Johnson，1970，p.219）。</p></div><h2 id="第10章-压力与紧张" tabindex="-1"><a class="header-anchor" href="#第10章-压力与紧张"><span>第10章 压力与紧张</span></a></h2><h3 id="感情创伤" tabindex="-1"><a class="header-anchor" href="#感情创伤"><span>感情创伤</span></a></h3><div class="hint-container note"><p class="hint-container-title">另一方面，就接纳而言，从十分讨人喜爱到完全崇拜之间我们的自尊水平并未提升。相反，从矛盾情感的较低端（4分左右）到出现接纳的较高端（7分左右）之间的些微变化却令我们更为敏感。随着人们越来越喜欢我们，我们的自我感觉也越来越良好，直到他们对我们的佩服得五体投地。这一过程有着演化学上的意义（Leary et al.，1998）；精细区分不同的接纳程度能让我们得到更多的生存资源和交配机会，这比监测敌人的敌意要实用得多。（毕竟就未来配偶的反应而论，轻微的厌烦和彻底的厌恶之间通常实际差别并不大！）所以，他人温和的拒绝带来的感受通常和更大程度的拒绝一样糟糕。但他人对我们接纳的减少可能影响更大，特别是发生在矛盾情感和主动拒绝这一范围之内——也就是说，喜欢我们的人们突然变得不怎么喜欢我们了。</p></div><h3 id="伴侣排斥" tabindex="-1"><a class="header-anchor" href="#伴侣排斥"><span>伴侣排斥</span></a></h3><div class="hint-container note"><p class="hint-container-title">人们为什么会刻意忽视自己的伴侣？排斥者们往往会冠冕堂皇地认为他们的排斥行为是一种惩罚伴侣、避免对抗或者在冲突之后平复心绪和低调处理的有效手段，而且他们通常还相信排斥有助于实现他们的人际目标（Sommer et al.，2001）。但是从排斥的本质来看，这样做通常会使被排斥者感到疑惑：为什么自己会被人忽视（Williams，2001）？当排斥的伴侣保持沉默时，只有极少的人会进行解释，这样被排斥的伴侣往往对排斥的原因一无所知。他们只知道自己被人拒绝了，他们体验到的情感更多是愤怒、沮丧和伤害，而不是内疚和悔恨。因此，被排斥者一般并不认为伴侣的退避行为是友善或者有效之举，相反，他们通常认为排斥只会损害他们的亲密关系（Zadro et al.，2008）。</p></div><div class="hint-container note"><p class="hint-container-title">所以，排斥是非常令人讨厌的体验，其引起敌对和顺从的几率是一样的。高自尊的人相对不太可能容忍排斥。如果遭遇冷遇，高自尊的人比低自尊的人更有可能结束与排斥者的人际关系，去寻找能更友好相处的新伴侣——可能正是因为这个原因，他们不太会遭遇沉默对待。相比之下，低自尊的人会经历更多的排斥，更可能心怀怨隙、对他人报以排斥（Sommer &amp; Rubin，2005）。低自尊的人不会离开那些排斥他们的人，而会继续接近却心存恨意。</p></div><div class="hint-container note"><p class="hint-container-title">总之，当他人排斥我们时，我们会感觉悲痛、愤怒和受伤，这类情感体验的核心成分是知觉到他人并不像我们期待的那样珍视与我们的关系。现在我们来考察另一种危及关系价值的现象，即认识到情敌正在勾引我们的爱侣。</p></div><h3 id="容易嫉妒的人" tabindex="-1"><a class="header-anchor" href="#容易嫉妒的人"><span>容易嫉妒的人</span></a></h3><div class="hint-container note"><p class="hint-container-title">这类疑虑产生的根源之一在于亲密关系中每个人适配价值的差距（Buss，2000）。如果伴侣一方比另一方条件更好，比如外貌上更有吸引力、更富有或更有才华，较弱一方的适配价值就较低，这就可能出现问题。居于劣势的伴侣有可能认为其他人更加适合自己的爱人，这就可能觉得自己的能力不足，当然低能的感觉不会表现在他/她生活的其他领域（或者与其他伙伴的交往之中）。这就是婚配要讲究门当户对的另一个原因，人们总是与具有类似适配价值的人共结连理（见第3章）：大多数人都想得到最合意的伴侣，但如果他们真的拥有了完美的伴侣，却又会因为伴侣能得到更优秀的伴侣而感觉到威胁。</p></div><h3 id="让人嫉妒的事物" tabindex="-1"><a class="header-anchor" href="#让人嫉妒的事物"><span>让人嫉妒的事物</span></a></h3><div class="hint-container note"><p class="hint-container-title">记得我们谈到过（第1章）男性会面临女性不曾遇到过的生殖困难：父系不确定。女性总是能确认某个孩子是否是亲生的，但男性却不能断定（不采用现代高科技）自己是否是孩子的生父，除非他完全相信自己的配偶没有和其他人有过性行为。而男人戴了绿帽子并且养育了另一个男人的后代，从演化的角度来看是非常失败的；那些养育别人的孩子却没有自己后代的人不可能是人类的先祖！的确，无法察觉伴侣不贞行为的演化成本非常之高，所以自然选择会偏利于那些对伴侣的忠贞特别敏感的男性，而不是那些不怎么警惕的男性（Haselton &amp; Buss，2000）。没有根据地怀疑伴侣的忠贞会造成感情破裂、令人痛苦，但在演化学的意义上，这点代价并不昂贵和危险，因为过于信任伴侣，不能察觉已出现的不贞行为，造成的损失更严重，后果更危险。于是，如今男性比女性有着更多的婚外情（Tafoya &amp; Spitzberg，2007），但在觉察伴侣欺骗的不贞问题上，男性比女性更准确（Andrews et al.，2008）。这种警惕有时是合乎情理的；在第9章我们曾提及，基因研究表明全世界有2%的“父亲”并不知道孩子的亲生父亲另有其人（Anderson，2006）。</p></div><div class="hint-container note"><p class="hint-container-title">对女性而言，如果男性要收回用以保护和庇佑她们及子女的资源，而女性能敏锐地觉察出任何苗头时，她们大概就能更成功地生养孩子。想当然地认为男性会忠于她们，而实际上并非如此，这会给女性带来风险，所以自然选择可能会偏利于那些经常怀疑男性真爱表白的女性。不客观地怀疑男性的忠诚会令她们厌烦并产生自我挫败感，但相信男伴的专一和忠诚而对方并非如此时，付出的代价可能更高。在我们祖先所处的远古时代，频繁而天真地与男性交配又遭抛弃的女子，并不如那些坚持考证男性是否忠贞的女子繁殖得更成功。因此，现代女性或许都是“极端谨慎的远古母系的后裔”，她们一般会慎重地低估其男伴的忠诚度（Haselton &amp; Buss，2000，p.83）。</p></div><div class="hint-container note"><p class="hint-container-title">这一切的结果是，男性一想到伴侣在性事上的不贞就会激起最强烈的嫉妒，而女性对伴侣感情上的不贞反应更强烈，伴侣感情上的不贞是指女性认为伴侣可能爱上了其他女人。当然两种不贞都会激起两性的嫉妒，只不过具有不同的演化学意义。对男性而言，伴侣爱上其他人对其成功繁殖的威胁并不大，性行为才更重要；如果伴侣爱上另一个男人，他的孩子仍能茁壮成长，但他肯定不愿意抚养其他男人的孩子。对女性而言，伴侣与其他人发生性关系并不是最可怕的，爱恋上别人才更危险；只要男方能继续供给必需的资源，即使他让其他女人怀孕，她的孩子仍能茁壮成长——但如果男方爱上其他女人，彻底地离开自己，那她孩子的前景就堪忧了。</p></div><h3 id="对嫉妒的反应" tabindex="-1"><a class="header-anchor" href="#对嫉妒的反应"><span>对嫉妒的反应</span></a></h3><div class="hint-container note"><p class="hint-container-title">男性和女性在对嫉妒的反应上通常也存在差异，这使得异性恋关系变得更加复杂。设想这样一种场景：聚会时，你起身去再要一些饮料，把你的恋人留在了一张双人沙发上。在你离开的时候，恋人的前男友或女友碰巧经过，小坐了会。当你拿着饮料回来时，刚好看到他们轻轻一吻来问候彼此。这时你会怎么办？当研究者让参与者观看上述场景的录像带，并测量他们的行为意图，结果发现男性和女性的反应不一样（Shettel-Neuber et al.，1978）。女性表示他们会设法改善亲密关系以应对情敌的侵扰；她们会刻意装作漠不关心，却使自己变得对伴侣更有吸引力来与情敌暗中较劲。相反，男性表示他们会努力维护自我形象；假装酒醉与情敌对峙或者威胁对方，并且还会追求别的女性。女性似乎关注于维护好现有的亲密关系，而男性则会考虑离开，通过征服新的恋人来医治受伤的自尊。</p></div><h3 id="宽恕" tabindex="-1"><a class="header-anchor" href="#宽恕"><span>宽恕</span></a></h3><div class="hint-container note"><p class="hint-container-title">不过，不管我们是谁，当宽恕的一些重要构成要素存在时，宽恕就更容易发生。第一个要素是谦卑、真诚的道歉。如果背叛者承认他们的错误，承担责任，为他们的不端行为表示羞愧、悔恨和自责以真诚地赎罪，并发誓未来表现更好，受害者更有可能宽恕那些背叛他们的人（Hannon et al.，2010；Tabak et al.，2011）。如果编造借口，道歉看起来不真诚，就不太可能得到受害者的宽恕。如果你曾有不端行为，亲密关系受损，你最好能认识到你的行为造成的伤害并道歉——但只有诚心实意地道歉才有效（Zechmeister et al.，2004）。</p></div>',228))])}const v=e(o,[["render",h]]),m=JSON.parse('{"path":"/read/o7oa4gxj/","title":"《亲密关系》","lang":"zh-CN","frontmatter":{"title":"《亲密关系》","createTime":"2025/09/05 19:57:35","permalink":"/read/o7oa4gxj/","badge":{"type":"info","text":"心理"},"description":"《亲密关系》 - [美] 罗兰·米勒 (Rowland S. Miller) 亲密关系与泛泛之交有什么区别？大丈夫与小女子真的般配吗？吸引力的秘密是什么？男人与女人真的是不同的动物吗？同性恋真的是由基因决定的吗？单亲家庭的孩子长大后更容易离婚吗……什么是爱情？由什么构成？能持续多久？两性在发生一夜情及选择终身伴侣上有什么差异？爱情和性欲是由不同的脑区控...","head":[["script",{"type":"application/ld+json"},"{\\"@context\\":\\"https://schema.org\\",\\"@type\\":\\"Article\\",\\"headline\\":\\"《亲密关系》\\",\\"image\\":[\\"\\"],\\"dateModified\\":\\"2026-01-08T07:10:31.000Z\\",\\"author\\":[]}"],["meta",{"property":"og:url","content":"https://www.ajohn.top/read/o7oa4gxj/"}],["meta",{"property":"og:site_name","content":"AJohn Blog"}],["meta",{"property":"og:title","content":"《亲密关系》"}],["meta",{"property":"og:description","content":"《亲密关系》 - [美] 罗兰·米勒 (Rowland S. Miller) 亲密关系与泛泛之交有什么区别？大丈夫与小女子真的般配吗？吸引力的秘密是什么？男人与女人真的是不同的动物吗？同性恋真的是由基因决定的吗？单亲家庭的孩子长大后更容易离婚吗……什么是爱情？由什么构成？能持续多久？两性在发生一夜情及选择终身伴侣上有什么差异？爱情和性欲是由不同的脑区控..."}],["meta",{"property":"og:type","content":"article"}],["meta",{"property":"og:locale","content":"zh-CN"}],["meta",{"property":"og:updated_time","content":"2026-01-08T07:10:31.000Z"}],["meta",{"property":"article:modified_time","content":"2026-01-08T07:10:31.000Z"}]]},"readingTime":{"minutes":112.88,"words":33864},"git":{"createdTime":1757318038000,"updatedTime":1767856231000,"contributors":[{"name":"zzyAJohn","username":"zzyAJohn","email":"1833302139@qq.com","commits":11,"avatar":"https://avatars.githubusercontent.com/zzyAJohn?v=4","url":"https://github.com/zzyAJohn"}]},"autoDesc":true,"filePathRelative":"read/social-science/Intimate Relationships.md","headers":[]}');export{v as comp,m as data};
